:root {
	--font-family: 'Inter', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 700;
	--font-size-body: 14px;
	--font-size-label: 16px;
	--font-size-header: 24px;
	--font-size-input: 16px;
	--font-size-error: 12px;

	/* Purple Themed Colors */
	--color-background: #f5f3ff; /* soft lavender */
	--color-card-bg: #ffffff;
	--color-card-border: #e0d7f8;
	--color-card-shadow: rgba(103, 80, 164, 0.12);
	--color-header: #3c1e74;
	--color-label: #4c1d95;
	--color-input-text: #1f1b2e;
	--color-input-border: #c4b5fd;
	--color-focus-border: #7c3aed;
	--color-submit-btn-bg: #7c3aed;
	--color-submit-btn-bg-hover: #6d28d9;
	--color-submit-btn-text: #ffffff;
	--color-error: #dc2626;
	--color-required: #dc2626;
	--color-test-notice-text: #7e22ce;
	--color-test-notice-bg: #f3e8ff;
	--color-test-notice-border: #e9d5ff;

	--border-radius-card: 12px;
	--border-radius-input: 8px;
	--submit-btn-height: 52px;

	--padding-card: 32px;
	--padding-form-input: 14px;
	--box-shadow-card: 0 8px 24px rgba(103, 80, 164, 0.15);
}

/* Body Background */
body {
	background-color: var(--color-background);
	font-family: var(--font-family);
	margin: 0;
	padding: 0;
	color: var(--color-input-text);
}

/* Form Container Card */
.card {
	max-width: 480px;
	margin: 40px auto;
	background-color: var(--color-card-bg);
	border-radius: var(--border-radius-card);
	box-shadow: var(--box-shadow-card);
	padding: var(--padding-card);
	border: 1px solid var(--color-card-border);
}

/* Header */
.card h1 {
	font-size: var(--font-size-header);
	font-weight: var(--font-weight-bold);
	color: var(--color-header);
	margin-bottom: 24px;
	text-align: center;
}

/* Test Notice */
.test-notice {
	background-color: var(--color-test-notice-bg);
	color: var(--color-test-notice-text);
	border: 1px solid var(--color-test-notice-border);
	border-radius: 6px;
	padding: 12px 16px;
	font-size: 14px;
	margin-bottom: 24px;
}

/* Label */
label {
	display: block;
	font-size: var(--font-size-label);
	font-weight: var(--font-weight-bold);
	color: var(--color-label);
	margin-bottom: 8px;
}

/* Input Field */
input[type="text"], input[type="email"] {
	width: 100%;
	padding: var(--padding-form-input);
	border: 1px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	font-size: var(--font-size-input);
	color: var(--color-input-text);
	box-sizing: border-box;
	transition: border-color 0.2s ease;
}

input[type="text"]:focus, input[type="email"]:focus {
	border-color: var(--color-focus-border);
	outline: none;
}

/* Error Message */
.error {
	color: var(--color-error);
	font-size: var(--font-size-error);
	margin-top: 6px;
}

/* Submit Button */
button[type="submit"] {
	width: 100%;
	height: var(--submit-btn-height);
	background-color: var(--color-submit-btn-bg);
	color: var(--color-submit-btn-text);
	font-weight: var(--font-weight-bold);
	font-size: 16px;
	border: none;
	border-radius: 10px;
	cursor: pointer;
	transition: background-color 0.2s ease;
}

button[type="submit"]:hover {
	background-color: var(--color-submit-btn-bg-hover);
}

/* Required Asterisk */
label .required {
	color: var(--color-required);
	margin-left: 4px;
}

