// Loop over input items and add a new field called 'myNewField' to the JSON of each one
var list = $input.all();

function base64Encode(originalString) {
  // Create a Buffer from the string
  const buffer = Buffer.from(originalString, 'utf8');
  // Convert the Buffer to a Base64 string
  const encodedString = buffer.toString('base64');
  return encodedString;
}
function redactWhatsapp(number) {
  if (!number || number.length < 7) return number; // too short to redact safely
  const start = number.slice(0, 4);
  const end = number.slice(-4);
  return `${start}****${end}`;
}
var newList = list.filter((r)=>{
  return !!r.json.nama_lengkap;
}).map((row)=> {
  var data = row.json
  var name = data.nama_lengkap
  var whatsapp = redactWhatsapp(data.whatsapp)
  var label = name + ' (' +whatsapp+')'
  return label;
})

return {names: newList}

