      width: 60px;
      padding: 8px;
      margin: 8px 0;
      border-radius: 6px;
      border: 1px solid #c4b5fd;
      text-align: center;
    }

    button {
      background-color: #7c3aed;
      color: white;
      border: none;
      padding: 12px 24px;
      border-radius: 8px;
      font-size: 16px;
      cursor: pointer;
      margin-top: 12px;
    }

    button:hover {
      background-color: #6d28d9;
    }

    .results {
      margin-top: 24px;
      font-size: 18px;
      color: #1f1b2e;
    }

    .results li {
      margin: 4px 0;
    }
  </style>
</head>
<body>
  <div class="container">
    <h1>üéâ Giveaway <br/> N8N ID Meetup #2</h1>
    <div id="formSection">
      <p>Paste the list of attendee names below (one per line):</p>
      <textarea id="nameInput" placeholder="e.g. John Doe&#10;Jane Smith&#10;Andi Prasetyo"></textarea>
      <p>Select how many names to pick:</p>
      <input type="number" id="countInput" value="5" min="1" />
      <br />
      <button onclick="pickNames()">Pick Random Name(s)</button>
    </div>
    <div id="rollingSection" style="display:none; margin-top:32px; text-align:center;">
      <div id="rollingNames" style="font-size:2rem; color:#7c3aed; min-height:2.5em; font-weight:bold;"></div>
      <div id="rollingCount" style="margin-top:8px; color:#6d28d9;"></div>
      <button id="backButton" style="display:none; margin-top:24px; background:#e0e7ff; color:#4c1d95; border:none; padding:10px 24px; border-radius:8px; font-size:15px; cursor:pointer;" onclick="backToInput()">Back to Input</button>
    </div>
  </div>

  <script src="https://cdn.jsdelivr.net/npm/canvas-confetti@1.6.0/dist/confetti.browser.min.js"></script>

  <script>
    // Daftar nama peserta (bisa diubah sesuai kebutuhan)
    const predefinedNames = {{ JSON.stringify($json?.names || []) }}

    // Isi textarea dengan daftar nama saat halaman dimuat
    window.onload = function() {
      document.getElementById('nameInput').value = predefinedNames.join('\n');
    };

    function pickNames() {
      const input = document.getElementById('nameInput').value.trim();
      const count = parseInt(document.getElementById('countInput').value);
      const resultsList = document.getElementById('resultsList');
      const formSection = document.getElementById('formSection');
      const rollingSection = document.getElementById('rollingSection');
      const rollingNames = document.getElementById('rollingNames');
      const rollingCount = document.getElementById('rollingCount');

      if (!input) {
        alert('Please enter at least one name.');
        return;
      }

      const names = input.split('\n').map(name => name.trim()).filter(name => name !== '');

      if (count > names.length) {
        alert('Not enough names to pick from.');
        return;
      }

      // Sembunyikan form, tampilkan rolling
      formSection.style.display = 'none';
      rollingSection.style.display = 'block';

      // Rolling animation
      let rollingInterval;
      let rollingDuration = 2000; // ms
      let rollingSpeed = 60; // ms
      let rollingNamesArr = [];
      let rollingStep = 0;

      if (count === 1) {
        // Rolling satu nama
        rollingInterval = setInterval(() => {
          const randomName = names[Math.floor(Math.random() * names.length)];
          rollingNames.textContent = randomName;
        }, rollingSpeed);
      } else {
        // Rolling beberapa nama sekaligus
        rollingInterval = setInterval(() => {
          rollingNamesArr = [];
          let tempNames = [...names];
          for (let i = 0; i < count; i++) {
            if (tempNames.length === 0) break;
            const idx = Math.floor(Math.random() * tempNames.length);
            rollingNamesArr.push(tempNames[idx]);
            tempNames.splice(idx, 1);
          }
          rollingNames.innerHTML = rollingNamesArr.map(n => `<div>${n}</div>`).join('');
        }, rollingSpeed);
      }
      rollingCount.textContent = 'Picking winner...';

      setTimeout(() => {
        clearInterval(rollingInterval);
        // Pilih pemenang sebenarnya
        const shuffled = names.sort(() => 0.5 - Math.random());
        const selected = shuffled.slice(0, count);
        rollingNames.innerHTML = selected.map(n => `<div style=\"color:#22c55e;\">${n}</div>`).join('');
        rollingCount.textContent = 'Winner' + (count > 1 ? 's' : '') + ' üéâ';

        // Tampilkan confetti
        confetti({
          particleCount: 120,
          spread: 90,
          origin: { y: 0.6 }
        });

        document.getElementById('backButton').style.display = 'inline-block';
      }, rollingDuration);
    }

    function backToInput() {
      document.getElementById('formSection').style.display = 'block';
      document.getElementById('rollingSection').style.display = 'none';
      document.getElementById('backButton').style.display = 'none';
      document.getElementById('resultsList').innerHTML = '';
      document.getElementById('rollingNames').textContent = '';
      document.getElementById('rollingCount').textContent = '';
    }
  </script>
</body>
</html>

