// This node creates a human-readable timestamp for the current schedule run
// It helps track when each content generation session was initiated

const today = new Date();
const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
const scheduleName = `${days[today.getDay()]} ${today.getHours()}:00`;

return [{
  json: {
    scheduleName: scheduleName,
    timestamp: new Date().toISOString(),
    workflowId: $workflow.id
  }
}];

// This node parses the user input and validates the format
// Expected format: 'topic - format' where format is 'single' or 'thread'
// If no format is specified, it defaults to 'auto'

try {
  const userInput = $json.message.text;
  let topic, formatType;

  if (userInput.includes(' - ')) {
    const parts = userInput.split(' - ');
    topic = parts[0].trim();
    formatType = parts[1].trim().toLowerCase();
  } else {
    // If no format specified, default to auto
    topic = userInput.trim();
    formatType = "auto";
  }

  // Validate format type
  const validFormats = ['single', 'thread', 'auto'];
  if (!validFormats.includes(formatType)) {
    throw new Error(`Invalid format type: ${formatType}. Use 'single', 'thread', or 'auto'`);
  }

  // Validate topic length
  if (topic.length < 3) {
    throw new Error('Topic too short. Please provide a more detailed topic.');
  }

  return [{
    json: {
      topic: topic,
      format_type: formatType,
      chat_id: $json.message.chat.id,
      username: $json.message.from.username,
      original_message: userInput,
      validation_status: 'passed'
    }
  }];
} catch (error) {
  return [{
    json: {
      error: error.message,
      validation_status: 'failed',
      chat_id: $json.message.chat.id
    }
  }];
}

