// This node validates the AI-generated content to ensure quality standards
// It checks character counts, structure, and content formatting

try {
  const aiOutput = $json.output || $json;

  // Validate AI output structure
  if (!aiOutput.platform_posts?.Twitter) {
    throw new Error('Invalid AI output: Missing Twitter platform data');
  }

  const twitterData = aiOutput.platform_posts.Twitter;
  let tweets = [];
  let contentType = 'unknown';

  // Handle thread content
  if (twitterData.thread && Array.isArray(twitterData.thread)) {
    tweets = twitterData.thread;
    contentType = 'thread';
    console.log('Processing thread with ' + tweets.length + ' tweets');
  }
  // Handle single tweet content
  else if (twitterData.single_post) {
    tweets = [{
      tweet_number: 1,
      post: twitterData.single_post,
      character_count: twitterData.single_post.length
    }];
    contentType = 'single';
    console.log('Processing single tweet');
  } else {
    throw new Error('No valid tweet content found in AI output');
  }

  // Validate each tweet
  const validatedTweets = [];
  const warnings = [];
  let criticalErrors = 0;

  tweets.forEach((tweet, index) => {
    const tweetNumber = index + 1;
    const content = tweet.post || '';
    const charCount = content.length;
    let tweetHasIssues = false;

    // Character count validation
    if (charCount < 240 || charCount > 265) {
      warnings.push('Tweet ' + tweetNumber + ': ' + charCount + ' characters (recommended 240-265)');
      // Only count as critical error if severely outside range
      if (charCount < 216 || charCount > 291) {
        criticalErrors++;
        tweetHasIssues = true;
      }
    }

    // Check for numbered format at start (e.g., "1/", "2/")
    if (/^\d+[\.\/]/.test(content.trim())) {
      warnings.push('Tweet ' + tweetNumber + ': Starts with numbering (not recommended)');
    }

    validatedTweets.push({
      ...tweet,
      tweet_number: tweetNumber,
      character_count: charCount,
      has_issues: tweetHasIssues,
      validation_passed: !tweetHasIssues
    });

    console.log('Tweet ' + tweetNumber + ': ' + charCount + ' chars - "' + content.substring(0, 50) + '..."');
  });

  // Calculate error percentage
  const errorPercentage = (criticalErrors / tweets.length) * 100;
  const shouldPass = errorPercentage <= 30; // Pass if 30% or fewer critical errors

  console.log('Validation summary: ' + criticalErrors + '/' + tweets.length + ' critical errors (' + errorPercentage.toFixed(1) + '%)');
  console.log('Warnings: ' + warnings.length + ' total warnings');

  // Only fail if critical errors exceed 30%
  if (!shouldPass) {
    throw new Error('Critical validation failed: ' + errorPercentage.toFixed(1) + '% error rate exceeds 30% threshold');
  }

  return [{
    json: {
      ...aiOutput,
      content_type: contentType,
      tweet_count: validatedTweets.length,
      tweets_array: validatedTweets,
      validation_summary: {
        critical_errors: criticalErrors,
        error_percentage: Math.round(errorPercentage * 10) / 10,
        warnings: warnings.length,
        passed_threshold: shouldPass,
        warning_details: warnings
      },
      validation_status: 'passed',
      processed_at: new Date().toISOString()
    }
  }];

} catch (error) {
  console.error('Content validation error:', error.message);
  return [{
    json: {
      error: error.message,
      validation_status: 'failed',
      processed_at: new Date().toISOString()
    }
  }];
}

// Create a formatted preview of the generated content for user approval
const tweets = $json.tweets_array;
const contentType = $json.content_type;

let previewText = `üìù **Generated ${contentType.toUpperCase()} Content**\n\n`;
previewText += `**Topic:** ${$node['Parse User Input'].json.topic}\n`;
previewText += `**Format:** ${contentType}\n`;
previewText += `**Post Count:** ${tweets.length}\n\n`;

// Add each tweet to the preview
tweets.forEach((tweet, index) => {
  const tweetNum = index + 1;
  previewText += `**Post ${tweetNum}** (${tweet.character_count} chars):\n`;
  previewText += `${tweet.post}\n\n`;
});

previewText += `---\n**Ready to post?**\nClick ‚úÖ to approve or ‚ùå to cancel`;

return [{
  json: {
    ...($json),
    preview_text: previewText
  }
}];

