const regex = /\[(https:\/\/dashboard\.uptimerobot\.com\/monitors\/(\d{9})\?[^\]]*)\]/;
const statusMap = {
  0: "paused",
  1: "not checked yet",
  2: "up",
  8: "seems down",
  9: "down"
};

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.monitorUrl = $input.first().json.text.match(regex)?.[1];
  item.json.monitorId = $input.first().json.text.match(regex)?.[2];
}

return $input.all();

const statusMap = {
  0: "paused",
  1: "not checked yet",
  2: "up",
  8: "seems down",
  9: "down"
};

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
item.json.monitorStatus = statusMap[$input.first().json.status] || "unknown"; // Fallback to "unknown" if invalid
item.json.lastStatusChange = $input.first().json.logs[0].datetime || "unknown";
item.json.lastStatusChangeReason = $input.first().json.logs[0].reason.detail || "unknown";
}

return $input.all();

