// Get the AI response from the previous node
const aiResponse = $('AI Agent').first().json.output;

// Split the response into lines for parsing
const lines = aiResponse.split('\n').filter(line => line.trim() !== '');

let word = '';
let chineseMeaning = '';
let examples = [];

// Parse each line to extract the needed information
let inExamples = false;
let currentExample = '';
let currentTranslation = '';

for (let i = 0; i < lines.length; i++) {
  const trimmedLine = lines[i].trim();

  // Extract the word (looking for "Word: **word**" format)
  if (trimmedLine.startsWith('Word:')) {
    // Remove "Word:" and extract text between ** markers
    word = trimmedLine.replace('Word:', '').trim();
    // Remove bold markdown formatting if present
    word = word.replace(/\*\*/g, '').trim();
  }

  // Extract Chinese meaning
  if (trimmedLine.startsWith('Chinese Meaning:')) {
    chineseMeaning = trimmedLine.replace('Chinese Meaning:', '').trim();
  }

  // Check if we've reached the Examples section
  if (trimmedLine === 'Examples:' || trimmedLine.startsWith('Examples:')) {
    inExamples = true;
    continue;
  }

  // Parse examples when we're in the examples section
  if (inExamples && trimmedLine) {
    // Check if this line is an English sentence (not a Chinese translation)
    // Chinese translations typically start with Chinese characters
    const isChineseTranslation = /^[\u4e00-\u9fff]/.test(trimmedLine);

    if (!isChineseTranslation && trimmedLine.length > 10) {
      // This is likely an English example sentence
      // Save the previous example if we have both parts
      if (currentExample && currentTranslation) {
        examples.push({
          sentence: currentExample,
          translation: currentTranslation
        });
      }

      // Start a new example
      currentExample = trimmedLine;
      currentTranslation = '';
    } else if (isChineseTranslation && currentExample) {
      // This is the Chinese translation for the current example
      currentTranslation = trimmedLine;
    }
  }
}

// Don't forget to add the last example if it exists
if (currentExample && currentTranslation) {
  examples.push({
    sentence: currentExample,
    translation: currentTranslation
  });
}

// Return the structured data
return {
  word: word,
  chineseMeaning: chineseMeaning,
  examples: examples
};
