parameters.jsCode:
// This node prepares the final JSON payload for the website POST request.
// It combines data from various upstream nodes.

const parsedLLMOutput = $('Function: Parse LLM Output').item.json;
const imageBinaryData = $('Merge Image Data').item.json.imageBinaryData;
const postContentFromWriter = $('LLM: Article Writer').item.json.text;

// Default values for website post, can be configured via workflow parameters or credentials
const defaultUserId = '{{ $connections.websiteApi.userId }}'; 
const defaultUsername = '{{ $connections.websiteApi.username }}';
const defaultBgColor = '#FFFFFF';
const defaultLinkAff = '';
const defaultPostType = 'General';
const defaultEmbeddedContent = '';

const outputItem = {
    json: {
        user_id: parsedLLMOutput.user_id || defaultUserId,
        username: parsedLLMOutput.username || defaultUsername,
        postTitle: parsedLLMOutput.postTitle,
        postContent: postContentFromWriter,
        bgColor: parsedLLMOutput.bg_color || defaultBgColor,
        LinkAff: parsedLLMOutput.LinkAff || defaultLinkAff,
        postType: parsedLLMOutput.postType || defaultPostType,
        embeddedContent: parsedLLMOutput.embeddedContent || defaultEmbeddedContent,
        tags1: parsedLLMOutput.tags1,
        tags2: parsedLLMOutput.tags2,
        imageBinaryData: imageBinaryData, // Pass binary data reference
        description: postContentFromWriter // Use full article as description for Telegram
    },
    // Ensure binary data is also passed through for the HTTP Request node
    binary: {
        'generated-image.png': imageBinaryData
    }
};

return [outputItem];

// This node parses the structured text output from the LLM.
// It extracts TITLE, EXCERPT, TAGS, TAGS2, and IMAGE_PROMPT.

const rawLLMText = $json.text || "";

// --- 1. Extract desired data using Markers ---
const startMarker = "---START_DATA---";
const endMarker = "---END_DATA---";
let articleDataText = rawLLMText;

const startIndex = rawLLMText.indexOf(startMarker);
const endIndex = rawLLMText.indexOf(endMarker, startIndex);

if (startIndex !== -1 && endIndex !== -1) {
    articleDataText = rawLLMText.substring(startIndex + startMarker.length, endIndex);
}

function sanitizeValue(value) {
    if (typeof value !== 'string') return "";
    return value.trim().replace(/^"|"$|^|$/g, '').trim();
}

// --- 2. Extract each Field ---
const fields = {
    TITLE: "",
    EXCERPT: "",
    TAGS: "",
    TAGS2: "",
    IMAGE_PROMPT: ""
};
const regex = /(?:^|\n)\**\s*(TITLE|EXCERPT|TAGS|TAGS2|IMAGE_PROMPT)\s*\**:\s*([\s\S]*?)(?=(?:\n)\**\s*[A-Z_]+:|$)/gi;

let match;
while ((match = regex.exec(articleDataText)) !== null) {
    const fieldName = match[1].trim().toUpperCase();
    const fieldValue = sanitizeValue(match[2]);
    if (fields.hasOwnProperty(fieldName)) {
        fields[fieldName] = fieldValue;
    }
}

// --- 3. Post-processing data correction (preventing data bleed) ---
const tagsMarker = 'TAGS:';
if (fields.EXCERPT && fields.EXCERPT.includes(tagsMarker)) {
    const parts = fields.EXCERPT.split(new RegExp(`\\s*${tagsMarker}`));
    fields.EXCERPT = parts[0];
    if (!fields.TAGS && parts.length > 1) {
        fields.TAGS = parts[1];
    }
}

const tags2Marker = 'TAGS2:';
if (fields.TAGS && fields.TAGS.includes(tags2Marker)) {
    const parts = fields.TAGS.split(new RegExp(`\\s*${tags2Marker}`));
    fields.TAGS = parts[0];
    if (!fields.TAGS2 && parts.length > 1) {
        fields.TAGS2 = parts[1];
    }
}

// --- 4. Cleanse TAGS and TAGS2 separately ---
const cleanedTags1 = fields.TAGS.split(',')
    .map(tag => tag.trim())
    .filter(tag => tag.length > 0)
    .map(tag => tag.replace(/^#/, '')); // Removes leading '#' for tags1

const cleanedTags2 = fields.TAGS2.split(',')
    .map(tag => tag.trim())
    .filter(tag => tag.length > 0)
    .map(tag => tag.startsWith('#') ? tag : `#${tag}`); // Ensures each tag in tags2 starts with '#'

// --- 5. Create JSON Object for export ---
const jsonOutput = {
    "postTitle": fields.TITLE,
    "postContent": fields.EXCERPT,
    "tags1": cleanedTags1,
    "tags2": cleanedTags2,
    "imagePrompt": fields.IMAGE_PROMPT
};

return [jsonOutput];

// This node analyzes the news content to determine the appropriate image type (Real or AI) and extracts relevant keywords for real images.

const responseText = $json.text || "";

const outputItem = {
    json: {
        type: "",
        keywords: []
    }
};

if (!responseText) {
    console.warn("Input text is undefined or null. Returning default empty values.");
    return [outputItem];
}

const lines = responseText.split('\n').map(line => line.trim()).filter(Boolean);

let foundType = "";
let foundKeywords = [];

for (let i = 0; i < lines.length; i++) {
    const currentLine = lines[i];

    if (currentLine.startsWith("ภาพจริง")) {
        foundType = "ภาพจริง";
        if (i + 1 < lines.length) {
            const nextLine = lines[i + 1];
            if (!nextLine.startsWith("ภาพ AI") && !nextLine.toLowerCase().startsWith("keywords:")) {
                foundKeywords = nextLine.split(',')
                                       .map(kw => kw.trim())
                                       .filter(Boolean);
                break;
            }
        }
    } else if (currentLine.startsWith("ภาพ AI")) {
        foundType = "ภาพ AI";
        break;
    }

    if (currentLine.toLowerCase().startsWith("keywords:")) {
        const keywordString = currentLine.substring("keywords:".length);
        foundKeywords = keywordString.split(',')
                                    .map(kw => kw.trim())
                                    .filter(Boolean);
        if (foundType) {
            break;
        }
    }
}

outputItem.json.type = foundType;
outputItem.json.keywords = foundKeywords;

return [outputItem];

for (const item of items) {
  if (item.binary && item.binary['generated-image.png']) {
    item.json.imageBinaryData = item.binary['generated-image.png'];
  }
}
return items;

