// --- DIGIPIN Generation Logic ---

// Constants for DIGIPIN generation
const DIGIPIN_GRID = [
    ['F', 'C', '9', '8'],
    ['J', '3', '2', '7'],
    ['K', '4', '5', '6'],
    ['L', 'M', 'P', 'T']
];

const BOUNDS = {
    minLat: 2.5,
    maxLat: 38.5,
    minLon: 63.5,
    maxLon: 99.5
};

/**
 * Encodes latitude and longitude into a DIGIPIN.
 * @param {number} lat The latitude.
 * @param {number} lon The longitude.
 * @returns {string} The generated DIGIPIN.
 * @throws {Error} If coordinates are out of bounds.
 */
function generateDigiPin(lat, lon) {
    if (lat < BOUNDS.minLat || lat > BOUNDS.maxLat || lon < BOUNDS.minLon || lon > BOUNDS.maxLon) {
        throw new Error(`Coordinates (Lat: ${lat}, Lon: ${lon}) are out of the valid range.`);
    }

    let minLat = BOUNDS.minLat, maxLat = BOUNDS.maxLat;
    let minLon = BOUNDS.minLon, maxLon = BOUNDS.maxLon;
    let digiPin = '';

    for (let level = 1; level <= 10; level++) {
        const latDiv = (maxLat - minLat) / 4;
        const lonDiv = (maxLon - minLon) / 4;
        let row = 3 - Math.floor((lat - minLat) / latDiv);
        let col = Math.floor((lon - minLon) / lonDiv);
        row = Math.max(0, Math.min(row, 3));
        col = Math.max(0, Math.min(col, 3));
        digiPin += DIGIPIN_GRID[row][col];
        if (level === 3 || level === 6) {
            digiPin += '-';
        }
        maxLat = minLat + latDiv * (4 - row);
        minLat = minLat + latDiv * (3 - row);
        minLon = minLon + lonDiv * col;
        maxLon = minLon + lonDiv;
    }
    return digiPin;
}


// --- n8n Item Processing ---
// Loop through each input item provided to the node.
for (const item of items) {
    try {
        // Get latitude and longitude from the input item's JSON data.
        // Assumes the previous node provides these fields.
        const lat = $input.first().json.query.lat;
        const lon = $input.first().json.query.lon;

        if (lat === undefined || lon === undefined) {
            throw new Error("Input item is missing 'latitude' or 'longitude' field.");
        }

        // Generate the DIGIPIN.
        const digipinResult = generateDigiPin(lat, lon);

        // Add the result to a new field called 'digipin' in the item's JSON.
        item.json.digipin = digipinResult;

    } catch (error) {
        // If an error occurs, add an 'error' field to the item.
        // This helps with debugging in the n8n UI.
        item.json.error = error.message;
    }
}

// Return all the modified items to the next node in the workflow.
return items;

/**
 * Decodes a DIGIPIN into latitude and longitude coordinates.
 * @param {string} digiPin The DIGIPIN to decode.
 * @returns {{latitude: string, longitude: string}} An object containing the latitude and longitude,
 * each formatted to 6 decimal places.
 * @throws {Error} If the DIGIPIN is invalid or contains invalid characters.
 */
const DIGIPIN_GRID = [
    ['F', 'C', '9', '8'],
    ['J', '3', '2', '7'],
    ['K', '4', '5', '6'],
    ['L', 'M', 'P', 'T']
];


const BOUNDS = {
    minLat: 2.5,
    maxLat: 38.5,
    minLon: 63.5,
    maxLon: 99.5
};
function getLatLngFromDigiPin(digiPin) {
    const pin = digiPin.replace(/-/g, ''); // Remove hyphens for processing
    if (pin.length !== 10) throw new Error('Invalid DIGIPIN: Must be 10 characters long after removing hyphens.');

    let minLat = BOUNDS.minLat;
    let maxLat = BOUNDS.maxLat;
    let minLon = BOUNDS.minLon;
    let maxLon = BOUNDS.minLon; // Corrected initial value here, should be BOUNDS.minLon

    // Recalculate maxLon for accurate initial bounds
    // Based on the original DIGIPIN generation, it should cover the whole range initially.
    maxLon = BOUNDS.maxLon;


    for (let i = 0; i < 10; i++) {
        const char = pin[i];
        let found = false;
        let ri = -1, ci = -1;

        for (let r = 0; r < 4; r++) {
            for (let c = 0; c < 4; c++) {
                if (DIGIPIN_GRID[r][c] === char) {
                    ri = r;
                    ci = c;
                    found = true;
                    break;
                }
            }
            if (found) break;
        }

        if (!found) throw new Error(`Invalid character '${char}' in DIGIPIN.`);

        const latDiv = (maxLat - minLat) / 4;
        const lonDiv = (maxLon - minLon) / 4;

        const lat1 = maxLat - latDiv * (ri + 1);
        const lat2 = maxLat - latDiv * ri;
        const lon1 = minLon + lonDiv * ci;
        const lon2 = minLon + lonDiv * (ci + 1);

        minLat = lat1;
        maxLat = lat2;
        minLon = lon1;
        maxLon = lon2;
    }

    const centerLat = (minLat + maxLat) / 2;
    const centerLon = (minLon + maxLon) / 2;

    return {
        latitude: centerLat.toFixed(6),
        longitude: centerLon.toFixed(6)
    };
}


const MY_DIGIPIN = $input.first().json.query.digipin; // <--- REPLACE THIS with your desired DIGIPIN

// Ensure there's at least one item to process, or create a dummy one
let outputItems = [];
if (items.length === 0) {
    // If no items come into the node, create a blank one to attach results to
    outputItems.push({ json: {} });
} else {
    // If items exist, clone them to add results
    outputItems = JSON.parse(JSON.stringify(items));
}

for (const item of outputItems) {
    try {
        const digipinToProcess = MY_DIGIPIN;

        if (!digipinToProcess) {
            throw new Error("No DIGIPIN specified for processing.");
        }

        // Decode the DIGIPIN
        const decodedCoords = getLatLngFromDigiPin(digipinToProcess);

        // Add the result to new fields in the item's JSON
        item.json.processedDigipin = digipinToProcess; // Optional: To see which digipin was processed
        item.json.decodedLatitude = decodedCoords.latitude;
        item.json.decodedLongitude = decodedCoords.longitude;

    } catch (error) {
        // If an error occurs, add an 'error' field to the item.
        item.json.error = error.message;
    }
}

// Return all the modified items to the next node in the workflow.
return outputItems;
