let inputType = '';

if ($json.hasOwnProperty("callback_query")) {
  const cbData = $json.callback_query.data || "";

  if (cbData.startsWith("style_")) {
    inputType = 'style';
  } else {
    inputType = 'callback_query';
  }

} else if (
  $json.hasOwnProperty("message") &&
  $json.message.text === "/start"
) {
  inputType = 'message';

} else if (
  $json.hasOwnProperty("message") &&
  $json.message.text &&
  $json.message.text.toLowerCase().startsWith("/help")
) {
  inputType = "help";

} else if (
  $json.hasOwnProperty("message") &&
  $json.message.text
) {
  inputType = 'text';
}

return [
  {
    json: {
      ...$json,
      inputType
    }
  }
];

