const rawArray = $input.first().json.data;

console.log("Raw Array Data:", rawArray); // Log the raw input data

// Step 1: Parse the outer JSON to access the inner data
let parsedData;
try {
  parsedData = JSON.parse(rawArray);
  console.log("Parsed Data:", parsedData); // Log the parsed data
} catch (e) {
  console.log("Error Parsing Outer Data:", e);
  return [{
    json: {
      chatInput: "Failed to parse outer JSON data."
    }
  }];
}

// Step 2: Extract the actual transcript data from the 'data' field
const innerData = parsedData.data;

// Step 3: Decode Unicode characters in the string
const decodedData = decodeURIComponent(innerData.replace(/\\u([0-9A-Fa-f]{4})/g, (match, p1) => {
  return String.fromCharCode(parseInt(p1, 16));
}));

console.log("Decoded Data:", decodedData); // Log the decoded data

// Step 4: Check if the decoded data is a valid transcript
if (!decodedData || decodedData.trim() === "") {
  console.log("Invalid or empty transcript data.");
  return [{
    json: {
      chatInput: "No valid transcript found."
    }
  }];
}

// Step 5: Return the final transcript text
return [{
  json: {
    chatInput: decodedData
  }
}];

const inputText = $input.first().json.output;

function getFieldValue(text, field) {
  // Updated to stop at "---" or end of string, instead of a non-whitespace line
  const regex = new RegExp(`${field}:\\s*([\\s\\S]*?)(?=\\n---|$)`, 'i');
  const match = text.match(regex);
  return match ? match[1].trim() : '';
}

