const raw = $json["output"] || "";

function formatSection(title, content) {
  if (!content) return "";
  return `<strong>${title}</strong><br>${content}<br><br>`;
}

// Replace \n with <br> for final formatting
let html = raw.replace(/\n/g, "<br>");

// Extract section content from RAW text
function extractFromRaw(title) {
  const regex = new RegExp(`${title}\\s*:?\\s*\\n([\\s\\S]*?)(\\n\\n|\\n[A-Z ]{3,}|$)`, "i");
  const match = raw.match(regex);
  return match && match[1] ? match[1].trim().replace(/\n/g, "<br>") : "";
}

// Extract Servings and Cook Time
function extractDetails() {
  let servingsMatch = raw.match(/Servings\s*[:\-]?\s*(.+)/i);
  let cookTimeMatch = raw.match(/Cook\s*Time\s*[:\-]?\s*(.+)/i);
  let servings = servingsMatch ? `SERVINGS: ${servingsMatch[1].trim()} üë•` : "";
  let cookTime = cookTimeMatch ? `COOK TIME: ${cookTimeMatch[1].trim()} üî•` : "";
  return [servings, cookTime].filter(Boolean).join("<br>");
}


// Title Line ‚Äî also handles **Markdown bold** if exists
const firstLineRaw = raw.split("\n")[0].replace("üçΩÔ∏è ", "").trim();
const firstLine = firstLineRaw.replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>");

const formatted = `
<div style="font-family:Arial, sans-serif; line-height:1.6">
  <p>üçΩÔ∏è ${firstLine}</p><br>
  ${formatSection("DETAILS", extractDetails())}
  ${formatSection("INGREDIENTS", extractFromRaw("INGREDIENTS"))}
  ${formatSection("INSTRUCTIONS", extractFromRaw("INSTRUCTIONS"))}
  ${formatSection("SPECIAL ADD-ONS", extractFromRaw("SPECIAL ADD-ONS"))}
  ${formatSection("SUGGESTED PAIRING", extractFromRaw("SUGGESTED PAIRING"))}
  ${formatSection("CHEF'S NOTE", extractFromRaw("CHEF'S NOTE"))}
</div>
`;

return [
  {
    json: {
      formattedHtml: formatted
    }
  }
];
