// Enhanced PII Detection with ML-like scoring and masking
const crypto = require('crypto');

// Enhanced PII patterns with confidence scoring
const piiPatterns = {
  // High confidence patterns
  ssn: {
    pattern: /\b\d{3}-\d{2}-\d{4}\b/g,
    confidence: 0.95,
    severity: 'critical',
    mask: true
  },
  creditCard: {
    pattern: /\b(?:\d{4}[\s-]?){3}\d{4}\b/g,
    confidence: 0.90,
    severity: 'critical', 
    mask: true
  },

  // Medium confidence patterns
  email: {
    pattern: /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g,
    confidence: 0.85,
    severity: 'high',
    mask: false
  },
  phone: {
    pattern: /\b(?:\+?1[-.\s]?)?\(?\d{3}\)?[-.\s]?\d{3}[-.\s]?\d{4}\b/g,
    confidence: 0.80,
    severity: 'medium',
    mask: true
  },

  // Context-aware patterns
  ipAddress: {
    pattern: /\b(?:\d{1,3}\.){3}\d{1,3}\b/g,
    confidence: 0.70,
    severity: 'medium',
    mask: false
  },
  zipCode: {
    pattern: /\b\d{5}(?:-\d{4})?\b/g,
    confidence: 0.60,
    severity: 'low',
    mask: false
  },

  // Enhanced patterns
  driversLicense: {
    pattern: /\b[A-Z]{1,2}\d{6,8}\b/g,
    confidence: 0.75,
    severity: 'high',
    mask: true
  },
  dateOfBirth: {
    pattern: /\b(?:0?[1-9]|1[0-2])[/-](?:0?[1-9]|[12]\d|3[01])[/-](?:19|20)\d{2}\b/g,
    confidence: 0.70,
    severity: 'medium',
    mask: true
  },

  // New patterns
  accountNumber: {
    pattern: /\b(?:account|acct)[\s#:]*\d{6,12}\b/gi,
    confidence: 0.85,
    severity: 'high',
    mask: true
  },
  medicalId: {
    pattern: /\b(?:patient|medical)[\s#:]*\d{6,10}\b/gi,
    confidence: 0.90,
    severity: 'critical',
    mask: true
  }
};

// Enhanced context detection
const contextPatterns = {
  financial: /\b(?:bank|credit|loan|mortgage|investment|portfolio)\b/gi,
  medical: /\b(?:patient|doctor|hospital|diagnosis|medication|treatment)\b/gi,
  legal: /\b(?:attorney|lawyer|case|litigation|settlement|contract)\b/gi,
  personal: /\b(?:family|spouse|children|address|home|personal)\b/gi
};

// Masking functions
function maskPII(text, pattern, maskChar = '*') {
  return text.replace(pattern, (match) => {
    if (match.length <= 4) return maskChar.repeat(match.length);
    return match.substring(0, 2) + maskChar.repeat(match.length - 4) + match.substring(match.length - 2);
  });
}

function generateSessionId() {
  return crypto.randomBytes(16).toString('hex');
}

const results = [];
const sessionId = generateSessionId();

for (const item of items) {
  const chatInput = item.json.body?.message || item.json.message || item.json.chatInput || JSON.stringify(item.json);
  const timestamp = new Date().toISOString();

  if (!chatInput) {
    results.push({
      json: {
        ...item.json,
        sessionId,
        timestamp,
        error: "No chat input found",
        piiDetected: false,
        detectedPatterns: [],
        routeToOrchestrator: 2,
        riskScore: 0,
        context: 'unknown'
      }
    });
    continue;
  }

  const detectedPatterns = [];
  let maskedContent = chatInput;
  let hasPII = false;
  let totalRiskScore = 0;
  let highestSeverity = 'low';

  // Detect context
  let detectedContext = 'general';
  for (const [contextType, pattern] of Object.entries(contextPatterns)) {
    if (pattern.test(chatInput)) {
      detectedContext = contextType;
      break;
    }
  }

  // Enhanced PII detection with scoring
  for (const [patternName, config] of Object.entries(piiPatterns)) {
    const matches = chatInput.match(config.pattern);
    if (matches && matches.length > 0) {
      hasPII = true;

      // Calculate risk score
      const patternRisk = config.confidence * matches.length;
      totalRiskScore += patternRisk;

      // Track highest severity
      const severityLevels = { low: 1, medium: 2, high: 3, critical: 4 };
      if (severityLevels[config.severity] > severityLevels[highestSeverity]) {
        highestSeverity = config.severity;
      }

      // Mask content if required
      if (config.mask) {
        maskedContent = maskPII(maskedContent, config.pattern);
      }

      detectedPatterns.push({
        type: patternName,
        count: matches.length,
        confidence: config.confidence,
        severity: config.severity,
        examples: config.mask ? 
          matches.slice(0, 1).map(m => maskPII(m, config.pattern)) : 
          matches.slice(0, 1), // Only 1 example for security
        masked: config.mask
      });
    }
  }

  // Determine routing with enhanced logic
  let routeToOrchestrator;
  let routingReason;

  if (!hasPII) {
    routeToOrchestrator = 2; // Cloud
    routingReason = "No PII detected - using cloud model";
  } else if (highestSeverity === 'critical' || totalRiskScore > 2.0) {
    routeToOrchestrator = 1; // Local
    routingReason = "Critical PII or high risk score - using local model";
  } else if (detectedContext === 'medical' || detectedContext === 'financial') {
    routeToOrchestrator = 1; // Local
    routingReason = "Sensitive context detected - using local model";
  } else {
    routeToOrchestrator = 1; // Local (default for any PII)
    routingReason = "PII detected - using local model";
  }

  // Enhanced logging and monitoring
  const processingMetrics = {
    processingTime: Date.now(),
    inputLength: chatInput.length,
    patternsChecked: Object.keys(piiPatterns).length,
    patternsDetected: detectedPatterns.length
  };

  results.push({
    json: {
      // Core data
      originalMessage: chatInput,
      maskedMessage: maskedContent,
      piiDetected: hasPII,
      detectedPatterns: detectedPatterns,

      // Enhanced routing
      routeToOrchestrator: routeToOrchestrator,
      routingReason: routingReason,

      // Risk assessment
      riskScore: Math.round(totalRiskScore * 100) / 100,
      highestSeverity: highestSeverity,
      context: detectedContext,

      // Security & compliance
      sessionId: sessionId,
      timestamp: timestamp,
      processingMetrics: processingMetrics,

      // Data classification
      dataClassification: highestSeverity === 'critical' ? 'confidential' : 
                         highestSeverity === 'high' ? 'internal' : 'public',

      // Compliance flags
      complianceFlags: {
        gdpr: detectedContext === 'personal' || hasPII,
        hipaa: detectedContext === 'medical',
        pci: detectedPatterns.some(p => p.type === 'creditCard'),
        sox: detectedContext === 'financial'
      },

      // Include safe original data
      ...item.json
    }
  });
}

// Log processing summary (without PII)
console.log(`Processing Summary:`);
console.log(`- Messages processed: ${results.length}`);
console.log(`- PII detected in: ${results.filter(r => r.json.piiDetected).length} messages`);
console.log(`- High-risk messages: ${results.filter(r => r.json.riskScore > 1.5).length}`);
console.log(`- Session ID: ${sessionId}`);

return results;

// Enhanced logging for compliance and monitoring
const auditEntries = [];
const timestamp = new Date().toISOString();

for (const item of items) {
  // Create comprehensive audit log (without actual PII content)
  const auditEntry = {
    timestamp: timestamp,
    sessionId: item.json.sessionId,
    messageId: require('crypto').randomBytes(8).toString('hex'),

    // Processing details
    processingRoute: item.json.routeToOrchestrator === 1 ? 'local' : 'cloud',
    routingReason: item.json.routingReason,

    // Risk assessment
    piiDetected: item.json.piiDetected || false,
    riskScore: item.json.riskScore || 0,
    highestSeverity: item.json.highestSeverity || 'none',
    context: item.json.context || 'general',

    // Pattern summary (no actual content)
    patternsSummary: {
      totalPatterns: item.json.detectedPatterns?.length || 0,
      patternTypes: item.json.detectedPatterns?.map(p => p.type) || [],
      severityLevels: item.json.detectedPatterns?.map(p => p.severity) || []
    },

    // Compliance flags
    complianceFlags: item.json.complianceFlags || {},
    dataClassification: item.json.dataClassification || 'public',

    // Performance metrics
    processingMetrics: item.json.processingMetrics || {},

    // User interaction (safe metadata only)
    userMetadata: {
      inputLength: item.json.originalMessage?.length || 0,
      responseGenerated: true,
      errorOccurred: false
    }
  };

  auditEntries.push(auditEntry);

  // Enhanced console logging for monitoring
  console.log(`=== PRIVACY-AWARE AI AUDIT LOG ===`);
  console.log(`Timestamp: ${timestamp}`);
  console.log(`Session: ${auditEntry.sessionId}`);
  console.log(`Route: ${auditEntry.processingRoute.toUpperCase()}`);
  console.log(`PII Status: ${auditEntry.piiDetected ? 'DETECTED' : 'CLEAN'}`);
  console.log(`Risk Score: ${auditEntry.riskScore}`);
  console.log(`Context: ${auditEntry.context}`);
  console.log(`Compliance: ${JSON.stringify(auditEntry.complianceFlags)}`);
  console.log(`Classification: ${auditEntry.dataClassification}`);
  console.log(`=====================================`);
}

// Generate summary metrics
const summary = {
  totalMessages: auditEntries.length,
  piiMessages: auditEntries.filter(e => e.piiDetected).length,
  localProcessing: auditEntries.filter(e => e.processingRoute === 'local').length,
  cloudProcessing: auditEntries.filter(e => e.processingRoute === 'cloud').length,
  highRiskMessages: auditEntries.filter(e => e.riskScore > 1.5).length,
  complianceBreaches: 0, // Track any compliance issues
  averageRiskScore: auditEntries.reduce((sum, e) => sum + e.riskScore, 0) / auditEntries.length
};

console.log(`\n=== SESSION SUMMARY ===`);
console.log(`Total Messages: ${summary.totalMessages}`);
console.log(`PII Detected: ${summary.piiMessages}`);
console.log(`Local Processing: ${summary.localProcessing}`);
console.log(`Cloud Processing: ${summary.cloudProcessing}`);
console.log(`High Risk: ${summary.highRiskMessages}`);
console.log(`Avg Risk Score: ${summary.averageRiskScore.toFixed(2)}`);
console.log(`=======================`);

return items.map((item, index) => ({
  json: {
    ...item.json,
    auditEntry: auditEntries[index],
    sessionSummary: summary,
    complianceStatus: 'compliant',
    privacyScore: 100 - (auditEntries[index].riskScore * 10) // Convert risk to privacy score
  }
}));

// Centralized error handling and recovery
const errors = [];
const recoveryActions = [];

for (const item of items) {
  try {
    // Check for processing errors
    if (item.json.error) {
      errors.push({
        timestamp: new Date().toISOString(),
        sessionId: item.json.sessionId,
        errorType: 'processing_error',
        errorMessage: item.json.error,
        severity: 'medium',
        recoveryAction: 'logged_and_continued'
      });
    }

    // Check for PII detection failures
    if (!item.json.detectedPatterns && item.json.piiDetected === undefined) {
      errors.push({
        timestamp: new Date().toISOString(),
        sessionId: item.json.sessionId,
        errorType: 'pii_detection_failure',
        errorMessage: 'PII detection did not complete properly',
        severity: 'high',
        recoveryAction: 'defaulted_to_local_processing'
      });

      // Recovery: Default to safe local processing
      item.json.piiDetected = true;
      item.json.routeToOrchestrator = 1;
      item.json.routingReason = 'Error recovery - defaulted to local';

      recoveryActions.push('defaulted_to_local_processing');
    }

    // Check for compliance violations
    if (item.json.complianceFlags) {
      const violations = Object.entries(item.json.complianceFlags)
        .filter(([key, value]) => value === true)
        .map(([key]) => key);

      if (violations.length > 0 && item.json.routeToOrchestrator !== 1) {
        errors.push({
          timestamp: new Date().toISOString(),
          sessionId: item.json.sessionId,
          errorType: 'compliance_violation',
          errorMessage: `Compliance-sensitive data routed to cloud: ${violations.join(', ')}`,
          severity: 'critical',
          recoveryAction: 'force_local_routing'
        });

        // Recovery: Force local processing
        item.json.routeToOrchestrator = 1;
        item.json.routingReason = 'Compliance violation recovery - forced local';

        recoveryActions.push('force_local_routing');
      }
    }

  } catch (error) {
    errors.push({
      timestamp: new Date().toISOString(),
      sessionId: item.json.sessionId || 'unknown',
      errorType: 'unexpected_error',
      errorMessage: error.message,
      severity: 'critical',
      recoveryAction: 'system_fallback'
    });

    // System fallback
    item.json = {
      ...item.json,
      error: 'System error - using safe defaults',
      piiDetected: true,
      routeToOrchestrator: 1,
      routingReason: 'System error recovery',
      riskScore: 10,
      highestSeverity: 'critical'
    };

    recoveryActions.push('system_fallback');
  }
}

// Log all errors and recoveries
if (errors.length > 0) {
  console.log(`\nðŸš¨ ERROR REPORT ðŸš¨`);
  console.log(`Total Errors: ${errors.length}`);
  console.log(`Recovery Actions: ${recoveryActions.length}`);

  errors.forEach((error, index) => {
    console.log(`\nError ${index + 1}:`);
    console.log(`  Type: ${error.errorType}`);
    console.log(`  Severity: ${error.severity}`);
    console.log(`  Message: ${error.errorMessage}`);
    console.log(`  Recovery: ${error.recoveryAction}`);
    console.log(`  Session: ${error.sessionId}`);
  });

  console.log(`\nðŸ”§ RECOVERY SUMMARY:`);
  const recoveryStats = recoveryActions.reduce((acc, action) => {
    acc[action] = (acc[action] || 0) + 1;
    return acc;
  }, {});
  console.log(JSON.stringify(recoveryStats, null, 2));
}

return items.map(item => ({
  json: {
    ...item.json,
    errorHandling: {
      errorsDetected: errors.length,
      recoveryActionsApplied: recoveryActions.length,
      systemHealth: errors.length === 0 ? 'healthy' : 
                   errors.filter(e => e.severity === 'critical').length > 0 ? 'critical' : 'degraded'
    }
  }
}));

// Real-time monitoring and alerting
const alerts = [];
const metrics = {
  timestamp: new Date().toISOString(),
  performance: {},
  security: {},
  compliance: {},
  system: {}
};

for (const item of items) {
  const processingTime = Date.now() - new Date(item.json.timestamp).getTime();

  // Performance monitoring
  metrics.performance = {
    averageProcessingTime: processingTime,
    piiDetectionAccuracy: item.json.detectedPatterns ? 100 : 0,
    routingEfficiency: item.json.routingReason ? 100 : 0,
    systemLatency: processingTime
  };

  // Security monitoring
  metrics.security = {
    piiLeakageRisk: item.json.routeToOrchestrator === 2 && item.json.piiDetected ? 100 : 0,
    dataClassificationAccuracy: item.json.dataClassification ? 100 : 0,
    privacyScore: item.json.privacyScore || 100,
    riskScore: item.json.riskScore || 0
  };

  // Compliance monitoring
  const complianceFlags = item.json.complianceFlags || {};
  metrics.compliance = {
    gdprCompliance: complianceFlags.gdpr && item.json.routeToOrchestrator === 1 ? 100 : 
                    complianceFlags.gdpr ? 0 : 100,
    hipaaCompliance: complianceFlags.hipaa && item.json.routeToOrchestrator === 1 ? 100 :
                     complianceFlags.hipaa ? 0 : 100,
    pciCompliance: complianceFlags.pci && item.json.routeToOrchestrator === 1 ? 100 :
                   complianceFlags.pci ? 0 : 100,
    overallCompliance: Object.values(complianceFlags).every(flag => 
      !flag || item.json.routeToOrchestrator === 1) ? 100 : 0
  };

  // System monitoring
  metrics.system = {
    errorRate: item.json.errorHandling?.errorsDetected > 0 ? 
               (item.json.errorHandling.errorsDetected / 1) * 100 : 0,
    recoveryRate: item.json.errorHandling?.recoveryActionsApplied > 0 ? 100 : 0,
    systemHealth: item.json.errorHandling?.systemHealth || 'healthy',
    uptime: 100 // Assume 100% for now
  };

  // Generate alerts
  if (metrics.security.piiLeakageRisk > 0) {
    alerts.push({
      level: 'CRITICAL',
      type: 'PII_LEAKAGE_RISK',
      message: 'PII detected but routed to cloud processing',
      timestamp: new Date().toISOString(),
      sessionId: item.json.sessionId,
      action: 'IMMEDIATE_REVIEW_REQUIRED'
    });
  }

  if (metrics.compliance.overallCompliance < 100) {
    alerts.push({
      level: 'HIGH',
      type: 'COMPLIANCE_VIOLATION',
      message: 'Compliance requirements not met',
      timestamp: new Date().toISOString(),
      sessionId: item.json.sessionId,
      details: complianceFlags,
      action: 'AUDIT_TRAIL_REVIEW'
    });
  }

  if (metrics.performance.averageProcessingTime > 5000) {
    alerts.push({
      level: 'MEDIUM',
      type: 'PERFORMANCE_DEGRADATION',
      message: `Processing time exceeded threshold: ${metrics.performance.averageProcessingTime}ms`,
      timestamp: new Date().toISOString(),
      sessionId: item.json.sessionId,
      action: 'PERFORMANCE_OPTIMIZATION_NEEDED'
    });
  }
}

// Display monitoring dashboard
console.log(`\nðŸ“Š REAL-TIME MONITORING DASHBOARD ðŸ“Š`);
console.log(`Timestamp: ${metrics.timestamp}`);
console.log(`\nðŸš€ PERFORMANCE METRICS:`);
console.log(`  Processing Time: ${metrics.performance.averageProcessingTime}ms`);
console.log(`  PII Detection: ${metrics.performance.piiDetectionAccuracy}%`);
console.log(`  Routing Efficiency: ${metrics.performance.routingEfficiency}%`);

console.log(`\nðŸ”’ SECURITY METRICS:`);
console.log(`  Privacy Score: ${metrics.security.privacyScore}%`);
console.log(`  Risk Score: ${metrics.security.riskScore}`);
console.log(`  PII Leakage Risk: ${metrics.security.piiLeakageRisk}%`);

console.log(`\nâš–ï¸ COMPLIANCE METRICS:`);
console.log(`  GDPR: ${metrics.compliance.gdprCompliance}%`);
console.log(`  HIPAA: ${metrics.compliance.hipaaCompliance}%`);
console.log(`  PCI: ${metrics.compliance.pciCompliance}%`);
console.log(`  Overall: ${metrics.compliance.overallCompliance}%`);

console.log(`\nðŸ› ï¸ SYSTEM HEALTH:`);
console.log(`  Error Rate: ${metrics.system.errorRate}%`);
console.log(`  Recovery Rate: ${metrics.system.recoveryRate}%`);
console.log(`  Health Status: ${metrics.system.systemHealth}`);
console.log(`  Uptime: ${metrics.system.uptime}%`);

if (alerts.length > 0) {
  console.log(`\nðŸš¨ ACTIVE ALERTS (${alerts.length}):`);
  alerts.forEach((alert, index) => {
    console.log(`  ${index + 1}. [${alert.level}] ${alert.type}`);
    console.log(`     ${alert.message}`);
    console.log(`     Action: ${alert.action}`);
    console.log(`     Session: ${alert.sessionId}`);
  });
} else {
  console.log(`\nâœ… NO ACTIVE ALERTS - SYSTEM OPERATING NORMALLY`);
}

console.log(`\n========================================`);

return items.map(item => ({
  json: {
    ...item.json,
    monitoring: {
      metrics: metrics,
      alerts: alerts,
      dashboardGenerated: true,
      lastCheck: new Date().toISOString()
    }
  }
}));

