// Pull each dataset using .all()
const overview        = $('Format Overview').all()[0].json;
const dateData        = $('Format Date').all().map(item => item.json);
const pageData        = $('Format Page').all().map(item => item.json);
const queryData       = $('Format Query').all().map(item => item.json);
const deviceData      = $("Format Devices").all().map(item => item.json);
const countryData     = $('Format Country').all().map(item => item.json);
const overviewPrev    = $('Format Overview - Previous month').all()[0].json;
const queryPrevData   = $('Format Query - Previous month').all().map(item => item.json);
const pagePrevData    = $('Format Page - Previous month').all().map(item => item.json);


// // Helper for subtitles
function pctSubtitle(curr, prev) {
  const diff  = curr - prev;
  const arrow = diff >= 0 ? '‚Üó' : '‚Üò';
  const col   = diff >= 0 ? 'green' : 'red';
  const pct   = prev === 0 ? 'N/A' : Math.abs(diff / prev * 100).toFixed(1) + '%';
  return `<p style='color:${col}'>${arrow} ${pct} vs last month</p>`;
}
function numSubtitle(curr, prev) {
  const diff  = curr - prev;
  const arrow = diff >= 0 ? '‚Üó' : '‚Üò';
  const col   = diff >= 0 ? 'green' : 'red';
  return `<p style='color:${col}'>${arrow} ${Math.abs(diff)} vs last month</p>`;
}

// helper to remove protocol+domain, leaving only the path
function stripPath(url) {
  return url.replace(/^https?:\/\/[^/]+/, '');
}

// just above your report definition, build a Set of previous-month queries:
const prevQueries = new Set(queryPrevData.map(q => q.query));

// Build the output
const report = {
  report_date: `${dateData[0].date} to ${dateData[dateData.length - 1].date}`,
  overview: {
    impressions: {
      value:    overview.impressions,
      subtitle: pctSubtitle(overview.impressions, overviewPrev.impressions),
    },
    clicks: {
      value:    overview.clicks,
      subtitle: pctSubtitle(overview.clicks,    overviewPrev.clicks),
    },
    average_ctr: {
      value:    (parseFloat(overview.ctr)*100).toFixed(2)+"%",
      subtitle: pctSubtitle(overview.ctr, overviewPrev.ctr),
    },
    average_position: {
      value:    parseFloat(overview.position).toFixed(1),
      subtitle: pctSubtitle(overview.position, overviewPrev.position),
    },
  },
  performance: {
    impressions: dateData.map(d => d.impressions),
    clicks:      dateData.map(d => d.clicks),
    labels:      dateData.map(d => d.date),
  },
  top_10_countries: {
    clicks:  countryData.sort((a, b) => b.clicks - a.clicks).slice(0,10).map(d => parseInt(d.clicks)),
    labels:  countryData.sort((a, b) => b.clicks - a.clicks).slice(0,10).map(d => d.country),
  },
  devices: {
    clicks:  deviceData.sort((a, b) => b.clicks - a.clicks).slice(0,10).map(d => parseInt(d.clicks)),
    labels:  deviceData.sort((a, b) => b.clicks - a.clicks).slice(0,10).map(d => d.device),
  },
  organic_keywords_ranking: {
    top_3: {
      value:    queryData.filter(q => q.position <= 3).length,
      subtitle: numSubtitle(
                   queryData.filter(q => q.position <= 3).length,
                   queryPrevData.filter(q => q.position <= 3).length
                ),
    },
    from_3_10: {
      value:    queryData.filter(q => q.position >= 4 && q.position <= 10).length,
      subtitle: numSubtitle(
                   queryData.filter(q => q.position >= 4 && q.position <= 10).length,
                   queryPrevData.filter(q => q.position >= 4 && q.position <= 10).length
                ),
    },
    from_11_20: {
      value:    queryData.filter(q => q.position >= 11 && q.position <= 20).length,
      subtitle: numSubtitle(
                   queryData.filter(q => q.position >= 11 && q.position <= 20).length,
                   queryPrevData.filter(q => q.position >= 11 && q.position <= 20).length
                ),
    },
    from_21_100: {
      value:    queryData.filter(q => q.position >= 21).length,
      subtitle: numSubtitle(
                   queryData.filter(q => q.position >= 21).length,
                   queryPrevData.filter(q => q.position >= 21).length
                ),
    },
  },
  top_5_pages: pageData
    .sort((a, b) => b.clicks - a.clicks)
    .slice(0,5)
    .map(d => ({
      page:       `<p style='max-width:200px;overflow:hidden;white-space:nowrap;text-overflow:ellipsis'><strong>${stripPath(d.page)}</strong></p>`,
      clicks:     d.clicks,
      impressions: d.impressions,
      ctr:         (parseFloat(d.ctr)*100).toFixed(2)+"%",
      position:    parseFloat(d.position).toFixed(1),
    })),
  top_5_keywords: queryData
    .sort((a, b) => b.clicks - a.clicks)
    .slice(0,5)
    .map(d => ({
      keyword:    `<p style='max-width:180px;overflow:hidden;white-space:nowrap;text-overflow:ellipsis'><strong>${d.query}</strong></p>`,
      clicks:     d.clicks,
      impressions: d.impressions,
      ctr:         (parseFloat(d.ctr)*100).toFixed(2)+"%",
      position:    parseFloat(d.position).toFixed(1),
    })),
  pages_who_lost_most_traffic_vs_last_month:pagePrevData
  .map(prev => {
    const curr = pageData.find(d => d.page === prev.page) || { clicks: 0 };
    const delta = prev.clicks - curr.clicks;            // positive = clicks lost
    return {
      page: prev.page,
      clicks: curr.clicks,
      clicks_last_month: prev.clicks,
      delta
    };
  })
  .sort((a, b) => b.delta - a.delta)                   // highest drops first
  .slice(0, 5)
  .map(({ page, clicks, clicks_last_month, delta }) => ({
    page: `<p style='max-width:200px;overflow:hidden;white-space:nowrap;text-overflow:ellipsis'><strong>${stripPath(page)}</strong></p>`,                             // removes protocol+domain
    clicks,
    clicks_last_month,
    delta:"-"+delta
  })),
  top_5_new_keywords_last_month: queryData
   // keep only queries not present last month
  .filter(d => !prevQueries.has(d.query))
  // sort by clicks descending
  .sort((a, b) => b.clicks - a.clicks)
  // take top 5
  .slice(0, 5)
  // format exactly like your other lists
  .map(d => ({
    keyword:    `<p style='max-width:180px;overflow:hidden;white-space:nowrap;text-overflow:ellipsis'><strong>${d.query}</strong></p>`,
    clicks:     d.clicks,
    impressions:d.impressions,
    ctr:         (parseFloat(d.ctr)*100).toFixed(2) + "%",
    position:    parseFloat(d.position).toFixed(1),
  }))
};
return [{ json: report }];
