// Get the raw output string from the previous node.
const rawOutput = $input.first().json.output;

// The AI often wraps JSON in Markdown code blocks (```json ... ```).
// We need to extract the pure JSON string from inside the fences.
// This regex will find the content between the fences. If no fences are found,
// it will fall back to using the entire rawOutput string.
const jsonStringMatch = rawOutput.match(/```json\s*([\s\S]*?)\s*```/);
const jsonString = jsonStringMatch ? jsonStringMatch[1] : rawOutput.trim();

// Now, try to parse the *cleaned* string.
try {
  const alerts = JSON.parse(jsonString);

  // Check if the result is a non-empty array.
  if (Array.isArray(alerts) && alerts.length > 0) {
    // Success! Pass the alerts to the next node.
    return [{ json: { alerts } }];
  }
} catch (e) {
  // This will catch errors if the cleaned string is still not valid JSON.
  console.error("NixGuard did not return a valid JSON array even after cleaning:", e);
  console.error("String that failed to parse:", jsonString);
}

// If parsing fails, it's not an array, or it's empty, return no items.
// This will correctly route the workflow to the 'false' branch of the IF node.
return [];

/**
 * WARNING: This is a simplified, lightweight Markdown to HTML converter.
 * It does NOT use a dedicated library and will only handle a limited subset of Markdown:
 * - Headings (h1, h2, h3)
 * - Bold text (**text**)
 * - Unordered list items (* item)
 * - Paragraphs (double newlines)
 * It will FAIL on complex cases like nested lists, tables, or code blocks.
 * For robust conversion, using the 'marked' library is strongly recommended.
 */

function simpleMarkdownToHtml(markdown) {
  let html = markdown
    // Escape HTML to prevent injection from the input
    .replace(/&/g, '&')
    .replace(/</g, '<')
    .replace(/>/g, '>');

  // Block Elements (order matters)
  html = html
    .replace(/^### (.*$)/gim, '<h3>$1</h3>')
    .replace(/^## (.*$)/gim, '<h2>$1</h2>')
    .replace(/^# (.*$)/gim, '<h1>$1</h1>')
    .replace(/^\* (.*$)/gim, '<li>$1</li>');

  // Inline Elements
  html = html
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/__(.*?)__/g, '<strong>$1</strong>') // Alternative bold
    .replace(/\*(.*?)\*/g, '<em>$1</em>')
    .replace(/_(.*?)_/g, '<em>$1</em>'); // Alternative italics

  // Paragraphs and Line Breaks
  // Wrap list items in <ul>
  html = html.replace(/<li>(.|\n)*?<li>/g, '<ul>$&');
  html = html.replace(/(<\/li>)(?!.*<li>)/g, '$1</ul>');
  // Convert remaining newlines to <br> or wrap in <p>
  html = html.replace(/\n/g, '<br>');

  return html;
}

const markdownSummary = $input.first().json.executive_summary;
const htmlSummary = simpleMarkdownToHtml(markdownSummary);

$input.first().json.html_summary = htmlSummary;

return $input.all();
