// Parse AI response
const aiResponse = $input.first().json.message.content;
let contentData;

try {
    // Try to parse as JSON
    contentData = JSON.parse(aiResponse);
} catch (error) {
    // If not JSON, create structured response
    contentData = {
        error: "Failed to parse AI response",
        rawResponse: aiResponse
    };
}

// Add metadata
const output = {
    success: !contentData.error,
    requestId: $('Process Input').first().json.requestId,
    topic: $('Process Input').first().json.topic,
    timestamp: new Date().toISOString(),
    content: contentData,
    metadata: {
        tone: $('Process Input').first().json.tone,
        language: $('Process Input').first().json.language,
        platforms: $('Process Input').first().json.platforms
    }
};

// Add performance predictions (mock data - replace with real analytics)
if (output.success && output.content) {
    Object.keys(output.content).forEach(platform => {
        if (output.content[platform] && typeof output.content[platform] === 'object') {
            output.content[platform].predictions = {
                engagementRate: Math.floor(Math.random() * 30) + 70 + "%",
                estimatedReach: Math.floor(Math.random() * 5000) + 1000,
                bestPostingTime: getOptimalPostingTime(platform)
            };
        }
    });
}

function getOptimalPostingTime(platform) {
    const times = {
        linkedin: "Tuesday-Thursday, 8-10 AM",
        twitter: "Weekdays, 9-10 AM, 7-9 PM",
        instagram: "Weekdays, 11 AM-1 PM, 7-9 PM",
        facebook: "Wednesday-Friday, 1-4 PM",
        tiktok: "Tuesday-Thursday, 6-10 AM, 7-9 PM"
    };
    return times[platform] || "Weekdays, 10 AM-12 PM";
}

return output;
