// Emergency keywords that trigger immediate response
const emergencyKeywords = [
    'chest pain', 'heart attack', 'stroke', 'can\'t breathe', 'breathing difficulty',
    'unconscious', 'severe bleeding', 'suicide', 'overdose', 'poisoning',
    'severe pain', 'allergic reaction', 'anaphylaxis', 'seizure'
];

// Extract user input
const userInput = $input.first().json.body?.query || $input.first().json.query || '';
const language = $input.first().json.body?.language || 'en';
const age = $input.first().json.body?.age || 'unknown';
const sessionId = $input.first().json.body?.sessionId || Math.random().toString(36).substring(7);

// Check for emergency
const lowerInput = userInput.toLowerCase();
const isEmergency = emergencyKeywords.some(keyword => lowerInput.includes(keyword));

// Categorize query type
let queryType = 'general';
if (lowerInput.includes('symptom') || lowerInput.includes('pain') || lowerInput.includes('feel')) {
    queryType = 'symptom';
} else if (lowerInput.includes('medication') || lowerInput.includes('medicine') || lowerInput.includes('drug')) {
    queryType = 'medication';
} else if (lowerInput.includes('appointment') || lowerInput.includes('doctor') || lowerInput.includes('schedule')) {
    queryType = 'appointment';
} else if (lowerInput.includes('wellness') || lowerInput.includes('health tip') || lowerInput.includes('prevent')) {
    queryType = 'wellness';
}

// Privacy notice
const privacyNotice = "This conversation is not stored and is for informational purposes only.";

// Prepare output
return {
    userInput,
    language,
    age,
    sessionId,
    isEmergency,
    queryType,
    privacyNotice,
    timestamp: new Date().toISOString(),
    disclaimer: "This is not medical advice. Consult healthcare professionals for medical concerns."
};

