// Compile final response
const input = $('Safety Check & Categorization').item.json;
const response = $input.first().json;

// Build structured response
const output = {
    success: true,
    sessionId: input.sessionId,
    timestamp: new Date().toISOString(),
    query: {
        original: input.userInput,
        type: input.queryType,
        isEmergency: input.isEmergency,
        language: input.language
    },
    response: {
        content: response.response,
        type: response.responseType,
        severity: response.severity || 'normal'
    },
    disclaimers: {
        medical: response.disclaimer || input.disclaimer,
        privacy: input.privacyNotice
    },
    resources: response.resources || null,
    followUp: {
        suggestedActions: getSuggestedActions(input.queryType, input.isEmergency),
        nextSteps: getNextSteps(input.queryType)
    }
};

// Helper functions
function getSuggestedActions(queryType, isEmergency) {
    if (isEmergency) return ['Call emergency services', 'Get immediate help'];

    const actions = {
        symptom: ['Track symptoms', 'Schedule doctor visit', 'Rest and monitor'],
        medication: ['Consult pharmacist', 'Read medication guide', 'Set reminders'],
        appointment: ['Call your provider', 'Prepare questions', 'Gather medical history'],
        wellness: ['Start healthy habits', 'Track progress', 'Set wellness goals'],
        general: ['Consult healthcare provider', 'Learn more', 'Stay informed']
    };

    return actions[queryType] || actions.general;
}

function getNextSteps(queryType) {
    const steps = {
        symptom: 'If symptoms persist or worsen, see a healthcare provider promptly.',
        medication: 'Always follow prescription instructions and consult your pharmacist.',
        appointment: 'Write down your concerns to discuss with your provider.',
        wellness: 'Small daily changes can lead to significant health improvements.',
        general: 'Your health is important - don\'t hesitate to seek professional help.'
    };

    return steps[queryType] || steps.general;
}

return output;
