const comments = $input.first().json.comments || [];
// CR√çTICO: Usar .all() para obtener TODAS las filas, no solo la primera
const allContactedUsersData = $('Read Contacted Users').all();
console.log('üîç DEBUG - Raw all contacted users:', JSON.stringify(allContactedUsersData, null, 2));
console.log('üîç DEBUG - Number of items from Read Contacted Users:', allContactedUsersData.length);

// Extraer todos los objetos JSON de todas las ejecuciones y aplanar en un solo array
let contactedUsersData = [];
allContactedUsersData.forEach((item, index) => {
  console.log(`üîç DEBUG - Processing item ${index}:`, JSON.stringify(item.json, null, 2));
  if (item.json) {
    if (Array.isArray(item.json)) {
      // Si es un array, agregar todos los elementos
      contactedUsersData.push(...item.json);
      console.log(`üîç DEBUG - Added ${item.json.length} items from array`);
    } else {
      // Si es un solo objeto, agregarlo
      contactedUsersData.push(item.json);
      console.log(`üîç DEBUG - Added 1 object`);
    }
  }
});

console.log('üîç DEBUG - Total extracted contacted users:', contactedUsersData.length);

// DEBUGGING DETALLADO
console.log('üîç DEBUG - Input comments found:', comments.length);
console.log('üîç DEBUG - Raw contacted users data:', JSON.stringify(contactedUsersData, null, 2));
console.log('üîç DEBUG - Contacted users data type:', typeof contactedUsersData);
console.log('üîç DEBUG - Contacted users data keys:', Object.keys(contactedUsersData));

// Handle different Google Sheets response formats
let contactedUsers = [];

// IMPORTANTE: Primero verificar si es un array directo (m√∫ltiples filas)
if (Array.isArray(contactedUsersData)) {
  contactedUsers = contactedUsersData;
  console.log('üîç DEBUG - Using direct array format (multiple rows)');
} 
// M√©todo 2: Respuesta con .values (formato com√∫n)
else if (contactedUsersData.values && Array.isArray(contactedUsersData.values)) {
  contactedUsers = contactedUsersData.values;
  console.log('üîç DEBUG - Using .values format');
} 
// M√©todo 3: Respuesta con .data
else if (contactedUsersData.data && Array.isArray(contactedUsersData.data)) {
  contactedUsers = contactedUsersData.data;
  console.log('üîç DEBUG - Using .data format');
}
// M√âTODO 4 CORREGIDO: Manejar objetos √∫nicos vs m√∫ltiples
else if (typeof contactedUsersData === 'object' && contactedUsersData !== null) {
  // CR√çTICO: Verificar si es UN SOLO objeto con comment_id (1 fila)
  if (contactedUsersData.comment_id || contactedUsersData.username) {
    contactedUsers = [contactedUsersData];
    console.log('üîç DEBUG - Converting single object to array (1 row)');
  } 
  // Si es un objeto pero SIN comment_id directo, puede ser m√∫ltiples objetos
  else {
    // Convertir a array pero tambi√©n verificar si los valores son objetos
    const values = Object.values(contactedUsersData);

    // Si los valores son objetos con comment_id, son m√∫ltiples filas
    if (values.length > 0 && typeof values[0] === 'object' && values[0]?.comment_id) {
      contactedUsers = values;
      console.log('üîç DEBUG - Converting object values to array (multiple rows from object)');
    } else {
      contactedUsers = values;
      console.log('üîç DEBUG - Converting object values to array (unknown format)');
    }
  }
}

// DEBUGGING EXTRA: Mostrar qu√© tipo de datos recibimos
console.log('üîç DEBUG - Raw data analysis:');
console.log('  - Is Array:', Array.isArray(contactedUsersData));
console.log('  - Type:', typeof contactedUsersData);
console.log('  - Has comment_id:', !!contactedUsersData?.comment_id);
console.log('  - Object keys:', contactedUsersData ? Object.keys(contactedUsersData) : 'null');

console.log('üîç DEBUG - Final contacted users array length:', contactedUsers.length);
console.log('üîç DEBUG - Final contacted users array:', JSON.stringify(contactedUsers, null, 2));

// Print first few rows to see the structure
if (contactedUsers.length > 0) {
  console.log('üîç DEBUG - First 5 rows structure:');
  contactedUsers.slice(0, 5).forEach((row, index) => {
    console.log(`  Row ${index}:`, JSON.stringify(row, null, 2));
    console.log(`  Row ${index} type:`, typeof row);
    console.log(`  Row ${index} keys:`, Object.keys(row || {}));
  });
}

// FUNCI√ìN PARA NORMALIZAR COMMENT IDs (cr√≠tico para n√∫meros largos)
function normalizeCommentId(id) {
  if (!id || id === null || id === undefined) return null;

  // Convertir a string y limpiar espacios
  let cleanId = String(id).trim();

  // Si es vac√≠o o es un header, retornar null
  if (cleanId === '' || cleanId === 'comment_id') return null;

  // IMPORTANTE: Manejar n√∫meros largos como strings para evitar problemas de precisi√≥n
  // Los comment IDs de Instagram son n√∫meros muy largos que pueden perder precisi√≥n
  return cleanId;
}

// Create a set of already contacted comment IDs
const contactedCommentIds = new Set();

contactedUsers.forEach((row, index) => {
  let commentId = null;

  console.log(`üîç DEBUG - Processing row ${index}:`, JSON.stringify(row, null, 2));

  // Skip header row if it exists
  if (typeof row === 'object' && row !== null && !Array.isArray(row)) {
    // Skip if this looks like a header row
    if (row.comment_id === 'comment_id' || row.username === 'username') {
      console.log(`‚ö†Ô∏è Skipping header row ${index}`);
      return;
    }

    // Handle object format (when Google Sheets returns objects)
    commentId = row.comment_id || row.commentId || row['comment_id'] || row['commentId'];
    console.log(`üîç DEBUG - Object format, raw comment_id: '${commentId}' (type: ${typeof commentId})`);
  } 
  // Handle array format (when Google Sheets returns arrays)
  else if (Array.isArray(row)) {
    // Skip if this looks like a header row
    if (row[0] === 'comment_id' || row[1] === 'username') {
      console.log(`‚ö†Ô∏è Skipping header array row ${index}`);
      return;
    }

    // comment_id is in Column A (index 0)
    commentId = row[0];
    console.log(`üîç DEBUG - Array format, raw comment_id: '${commentId}' (type: ${typeof commentId})`);
  }

  // Normalize and validate comment ID
  const normalizedId = normalizeCommentId(commentId);
  if (normalizedId) {
    contactedCommentIds.add(normalizedId);
    console.log(`‚úÖ Added contacted comment ID: '${normalizedId}' (length: ${normalizedId.length})`);
  } else {
    console.log(`‚ö†Ô∏è Row ${index} has no valid comment_id:`, commentId);
  }
});

console.log('üîç DEBUG - Total contacted comment IDs found:', contactedCommentIds.size);
console.log('üîç DEBUG - Contacted comment IDs list:', Array.from(contactedCommentIds));

// Filter new comments - exclude already contacted ones
const newComments = comments.filter(comment => {
  const rawCommentId = comment.id;
  const normalizedCommentId = normalizeCommentId(rawCommentId);

  if (!normalizedCommentId) {
    console.log(`‚ö†Ô∏è Invalid comment ID found: ${rawCommentId}`);
    return false;
  }

  const isAlreadyContacted = contactedCommentIds.has(normalizedCommentId);

  console.log(`üîç DEBUG - Checking comment ID: '${normalizedCommentId}' (raw: ${rawCommentId}, type: ${typeof rawCommentId}), Already contacted: ${isAlreadyContacted}`);

  // Debug: Show all contacted IDs for comparison
  if (isAlreadyContacted) {
    console.log(`üîç DEBUG - Comment ID '${normalizedCommentId}' found in contacted list`);
  } else {
    console.log(`üîç DEBUG - Comment ID '${normalizedCommentId}' NOT found in contacted list`);
    console.log(`üîç DEBUG - All contacted IDs: [${Array.from(contactedCommentIds).join(', ')}]`);
  }

  return !isAlreadyContacted;
});

console.log('üîç DEBUG - New comments after filter:', newComments.length);

// Log the filtering results with emojis
console.log(`üìä Total comments retrieved: ${comments.length}`);
console.log(`‚úÖ Already contacted comments: ${contactedCommentIds.size}`);
console.log(`üÜï New comments to process: ${newComments.length}`);

// Log details of new comments
newComments.forEach((comment, index) => {
  console.log(`üÜï New comment ${index + 1}: ID='${comment.id}', User='${comment.user?.username || 'unknown'}', Text='${comment.text?.substring(0, 50) || 'no text'}...'`);
});

if (newComments.length === 0) {
  console.log('üò¥ No new comments found. Waiting for next check...');
  console.log('üîÑ Workflow will continue monitoring in 15 minutes...');

  // Return a special object to indicate no processing needed
  return [{
    json: {
      noNewComments: true,
      summary: `üìä Monitoring complete - Found ${comments.length} total comments, ${contactedCommentIds.size} already contacted, 0 new comments to process`,
      timestamp: new Date().toISOString(),
      status: 'no_new_comments'
    }
  }];
}

// Transform each new comment into a separate item for processing
return newComments.map(comment => ({
  json: {
    commentId: normalizeCommentId(comment.id),
    commentText: comment.text,
    userId: comment.user?.id || 'unknown',
    username: comment.user?.username || 'unknown',
    timestamp: comment.timestamp || new Date().toISOString(),
    replyMessage: $('Configure Post & Message').first().json.replyMessage,
    postUrl: $('Configure Post & Message').first().json.postUrl,
    profileUsername: $('Configure Post & Message').first().json.profileUsername
  }
}));

// Log failed replies for debugging
const failedReply = {
  userId: $('Filter New Comments').item.json.userId,
  username: $('Filter New Comments').item.json.username,
  commentId: $('Filter New Comments').item.json.commentId,
  error: $json.error || 'Unknown error',
  timestamp: new Date().toISOString()
};

console.log('‚ùå Failed to send reply:', failedReply);

return [{ json: failedReply }];

// Summary of the monitoring session with proper error handling
let totalComments = 0;
let newComments = 0;
let successfulReplies = 0;
let failedReplies = 0;
let monitoredPostUrl = '';

try {
  // Get total comments (handle if node wasn't executed)
  const getPostCommentsData = $('Get Post Comments').first();
  totalComments = getPostCommentsData?.json?.comments?.length || 0;
} catch (error) {
  console.log('‚ö†Ô∏è Could not get total comments data:', error.message);
  totalComments = 0;
}

try {
  // Get new comments (handle if node wasn't executed)
  const filterNewCommentsData = $('Filter New Comments').all();
  newComments = filterNewCommentsData?.length || 0;
} catch (error) {
  console.log('‚ö†Ô∏è Could not get new comments data:', error.message);
  newComments = 0;
}

try {
  // Get successful replies (handle if node wasn't executed)
  const recordContactedData = $('Record Contacted User').all();
  successfulReplies = recordContactedData?.length || 0;
} catch (error) {
  console.log('‚ö†Ô∏è Could not get successful replies data:', error.message);
  successfulReplies = 0;
}

try {
  // Get failed replies (handle if node wasn't executed)
  const logFailedData = $('Log Failed Reply').all();
  failedReplies = logFailedData?.length || 0;
} catch (error) {
  console.log('‚ö†Ô∏è Could not get failed replies data:', error.message);
  failedReplies = 0;
}

try {
  // Get monitored post URL
  const configData = $('Configure Post & Message').first();
  monitoredPostUrl = configData?.json?.postUrl || 'Unknown URL';
} catch (error) {
  console.log('‚ö†Ô∏è Could not get post URL:', error.message);
  monitoredPostUrl = 'Unknown URL';
}

const summary = {
  totalComments,
  newComments,
  successfulReplies,
  failedReplies,
  monitoredPostUrl,
  timestamp: new Date().toISOString(),
  status: 'completed'
};

console.log('üìà Monitoring Summary:', JSON.stringify(summary, null, 2));

// Generate appropriate emoji and message
const emoji = successfulReplies > 0 ? 'üéâ' : failedReplies > 0 ? '‚ö†Ô∏è' : totalComments > 0 ? 'üëÄ' : 'üò¥';
let message = `${emoji} Monitoring cycle complete!`;

if (totalComments > 0) {
  message += ` Found ${totalComments} total comments`;

  if (newComments > 0) {
    message += `, ${newComments} new comments`;

    if (successfulReplies > 0) {
      message += `, sent ${successfulReplies} replies successfully`;
    }

    if (failedReplies > 0) {
      message += `, ${failedReplies} failed`;
    }
  } else {
    message += ', no new comments to process';
  }
} else {
  message += ' No comments found or error retrieving comments';
}

console.log(message);
console.log('‚è∞ Next check in 15 minutes...');
console.log('üîÑ Workflow will continue monitoring...');

return [{
  json: {
    summary,
    message,
    continueMonitoring: true
  }
}];
