// üßπ INSTAGRAM VIDEO CONTENT CLEANER
// Critical: Prevents 'JSON parameter needs to be valid JSON' errors
let content = $('üìä Content Database (Video)').item.json.instagram_reel;

content = content
  .replace(/[\n\r\t]+/g, ' ')    // Replace all line breaks and tabs with spaces
  .replace(/\s{2,}/g, ' ')       // Replace multiple spaces with single space
  .trim();                       // Remove leading/trailing whitespace

return [{
  json: {
    ...items[0].json,
    instagram_reel: content
  }
}];

// üìä COMPREHENSIVE CONTENT VALIDATION & PREPARATION
const item = $input.first();
const data = item.json;

// Validation results
const validation = {
  hasContent: false,
  hasMedia: false,
  platforms: [],
  errors: [],
  warnings: []
};

// Content fields mapping
const contentFields = {
  instagram: 'instagram_caption',
  twitter: 'twitter single',
  linkedin: 'linkedin_post',
  facebook: 'faceboook'
};

// Media fields
const mediaFields = ['postiz image', 'postiz video'];

// Validate content for each platform
for (const [platform, field] of Object.entries(contentFields)) {
  if (data[field] && data[field].trim().length > 0) {
    validation.platforms.push(platform);
    validation.hasContent = true;
  } else {
    validation.warnings.push(`Missing ${platform} content in field '${field}'`);
  }
}

// Validate media availability
for (const field of mediaFields) {
  if (data[field] && data[field].trim().length > 0) {
    validation.hasMedia = true;
    break;
  }
}

if (!validation.hasMedia) {
  validation.warnings.push('No media files available (image or video)');
}

// Critical validations
if (!validation.hasContent) {
  validation.errors.push('No content available for any platform');
}

if (validation.platforms.length === 0) {
  validation.errors.push('No valid platforms found with content');
}

// Prepare cleaned content for each platform
const cleanedContent = {};
for (const [platform, field] of Object.entries(contentFields)) {
  if (data[field]) {
    // Enhanced content cleaning
    let content = data[field]
      .replace(/[\n\r\t]+/g, ' ')           // Line breaks and tabs
      .replace(/\s{2,}/g, ' ')             // Multiple spaces
      .replace(/["']/g, (match) => match === '"' ? '\"' : "'")  // Escape quotes
      .replace(/[\\]/g, '\\\\')             // Escape backslashes
      .trim();                             // Trim whitespace

    // Platform-specific length validation
    const limits = {
      twitter: 280,
      instagram: 2200,
      linkedin: 3000,
      facebook: 63206
    };

    if (content.length > limits[platform]) {
      validation.warnings.push(`${platform} content exceeds ${limits[platform]} characters (${content.length})`);
      content = content.substring(0, limits[platform] - 3) + '...';
    }

    cleanedContent[platform] = content;
  }
}

return [{
  json: {
    originalData: data,
    validation: validation,
    cleanedContent: cleanedContent,
    recordId: data.id,
    processedAt: new Date().toISOString(),
    mediaAvailable: {
      image: !!data['postiz image'],
      video: !!data['postiz video'],
      imagePath: data['postiz image'] || null,
      videoPath: data['postiz video'] || null
    }
  }
}];

// üìä FIXED RESULTS PROCESSOR (MINIMAL CHANGES)
const items = $input.all();

// Integration ID to platform mapping (from your working Switch node)
const integrationToPlatform = {
  'cmcotolny0001pnal2i86mkrh': { platform: 'instagram', account: 'main' },
  'cmcg6ifpn001hmx9gakul0358': { platform: 'x', account: 'alt' },
  'cmcf026ts0001mx9g6o8t5xc4': { platform: 'x', account: 'main' },
  'cmcf02lp60003mx9gv2yff255': { platform: 'linkedin', account: 'main' },
  'cmcf054k4000bmx9gg9va2ce1': { platform: 'facebook', account: 'main' },
  'cmcouv8y00001kub0bb6cchea': { platform: 'youtube', account: 'main' }
};

const results = {
  totalPosts: items.length,
  successful: 0,
  failed: 0,
  platforms: {},
  errors: [],
  postIds: [],
  executionSummary: {
    recordId: null,
    startTime: new Date().toISOString(),
    platforms: [],
    overallStatus: 'success'
  }
};

// Process each posting result
for (const item of items) {
  const response = item.json;

  // Get platform info from integration ID in the response
  const platformInfo = integrationToPlatform[response.integration] || { platform: 'unknown', account: 'main' };
  const platform = platformInfo.platform;
  const account = platformInfo.account;

  if (!results.platforms[platform]) {
    results.platforms[platform] = {
      attempted: 0,
      successful: 0,
      failed: 0,
      accounts: {}
    };
  }

  results.platforms[platform].attempted++;

  if (!results.platforms[platform].accounts[account]) {
    results.platforms[platform].accounts[account] = {
      status: 'unknown',
      postId: null,
      error: null,
      integrationId: response.integration
    };
  }

  // Check if the post was successful - Postiz returns 'postId' on success
  if (response.postId && response.postId.length > 0) {
    results.successful++;
    results.platforms[platform].successful++;
    results.platforms[platform].accounts[account].status = 'success';
    results.platforms[platform].accounts[account].postId = response.postId;
    results.postIds.push({
      platform: platform,
      account: account,
      postId: response.postId,
      integrationId: response.integration,
      url: response.url || null
    });
  } else {
    results.failed++;
    results.platforms[platform].failed++;
    results.platforms[platform].accounts[account].status = 'failed';

    // Extract error message
    let errorMessage = 'Unknown error';
    if (response.error) {
      errorMessage = typeof response.error === 'string' ? response.error : JSON.stringify(response.error);
    } else if (response.message) {
      errorMessage = response.message;
    } else if (!response.postId) {
      errorMessage = 'No post ID returned from Postiz';
    }

    results.platforms[platform].accounts[account].error = errorMessage;
    results.errors.push({
      platform: platform,
      account: account,
      error: errorMessage,
      integrationId: response.integration,
      details: response
    });
  }
}

// Set overall status
if (results.failed > 0) {
  results.executionSummary.overallStatus = results.successful > 0 ? 'partial' : 'failed';
}

// Try to get record ID from Airtable node
try {
  const airtableData = $('üìä Content Database (Posts)1').first();
  if (airtableData && airtableData.json.id) {
    results.executionSummary.recordId = airtableData.json.id;
  }
} catch (e) {
  console.log('Could not get record ID from Airtable node');
}

results.executionSummary.platforms = Object.keys(results.platforms);

// Add debug info
results.debug = {
  itemCount: items.length,
  sampleResponse: items[0]?.json || null,
  integrationMappings: Object.keys(integrationToPlatform).length,
  platformsProcessed: Object.keys(results.platforms)
};

return [{ json: results }];
