// Get the jobs array from the previous node
const jobs = items[0].json.output.jobs;

// Map each job to a structured item
return jobs.map(job => {
  return {
    json: {
      title: job.title,
      company: job.company,
      location: job.location,
      salary: job.salary,
      job_type: job.job_type,
      benefits: job.benefits.length > 0 ? job.benefits.join(", ") : "No benefits listed",
      description: job.description,

      // Optional: a full text block if needed for markdown, email or summaries
      full_details: `
üß© *${job.title}*  
