// Get your input
const input = items[0].json.output;

// Map through trending_hashtags
const updatedHashtags = input.trending_hashtags.map(tag => {
  return {
    hashtag: tag.hashtag,
    uses: tag.uses.toString(),
    reach: tag.reach.toString()
  };
});

// Return the updated structure
return [
  {
    json: {
      trending_hashtags: updatedHashtags
    }
  }
];
