// Input: 1 item with `output` containing an array of product objects
const products = items[0].json.output;

// Return each product as its own item for Google Sheets
return products.map(product => {
  return {
    json: {
      name: product.name,
      description: product.description,
      price: product.price,
      url: product.url
    }
  };
});

