// Get the array inside the 'output' key
const customers = items[0].json.output;

// Map each customer to a separate item
return customers.map(customer => {
  return { json: customer };
});

// Loop through all incoming customers
const output = [];

for (const item of items) {
  const customerName = item.json.customer_name;
  const customerEmail = item.json.customer_email;  // Added email extraction
  const orders = item.json.orders;

  for (const order of orders) {
    output.push({
      json: {
        customer_name: customerName,
        customer_email: customerEmail,  // Added email in output
        amount: order.amount,
        date: order.date
      }
    });
  }
}

return output;
