const reviews = items[0].json.output; // The agent output you provided
let promoters = 0;
let passives = 0;
let detractors = 0;

for (const review of reviews) {
  const starRating = review.rating;

  // Convert 1â€“5 stars to 0â€“10 scale
  const score = starRating * 2;

  // Categorize into NPS groups
  if (score >= 9) {
    promoters += 1;
  } else if (score >= 7) {
    passives += 1;
  } else {
    detractors += 1;
  }
}

// NPS Calculation
const total = promoters + passives + detractors;
const nps = total > 0 ? ((promoters - detractors) / total) * 100 : 0;

return [
  {
    json: {
      totalResponses: total,
      promoters,
      passives,
      detractors,
      nps: Math.round(nps),
      message: `NPS Score is ${Math.round(nps)} based on ${total} responses.`
    }
  }
];

