// Fetch all incoming items
const items = $input.all();

// Extract the raw row data (each item.json is one row)
const rawRows = items.map(item => item.json);

// Bundle everything into a single field
const payload = { rows: rawRows };

// Return a single output item whose json contains your full dataset
return [{ json: { data: payload } }];

/**
 * Normalize raw AI JSON response (possibly wrapped in ```json blocks).
 * To be used inside an n8n Function/Code node.
 * Input item should have raw output under `item.json.output`.
 */
function normalizeAI(item) {
  let raw = item.json.output;
  if (typeof raw !== 'string') {
    throw new Error('Missing AI output string');
  }

  // 1. Strip ```json and backticks
  raw = raw
    .trim()
    .replace(/^```json\s*/, '')
    .replace(/```$/, '')
    .trim();

  // 2. Remove stray backslashes (if necessary)
  raw = raw.replace(/\\/g, '');

  // 3. Parse JSON
  let report;
  try {
    report = JSON.parse(raw);
  } catch (e) {
    throw new Error('Failed to parse AI JSON: ' + e.message);
  }

  // 4. Normalize numeric fields
  const toNum = x => (x !== undefined ? Number(x) : x);

  if (report.summary) {
    report.summary.total_quantity_sold = toNum(report.summary.total_quantity_sold);
    report.summary.total_revenue = toNum(report.summary.total_revenue);
    report.summary.total_profit = toNum(report.summary.total_profit);
    report.summary.average_profit_margin = toNum(report.summary.average_profit_margin);
  }

  const normalizeList = (arr, key) => {
    if (!Array.isArray(arr)) return [];
    return arr.map(i => ({
      ...i,
      [key]: toNum(i[key])
    }));
  };

  report.top_dishes_by_quantity = normalizeList(report.top_dishes_by_quantity, 'quantity_sold');
  report.top_dishes_by_revenue = normalizeList(report.top_dishes_by_revenue, 'total_revenue');

  if (report.margin_extremes) {
    if (report.margin_extremes.highest) {
      report.margin_extremes.highest.calculated_profit_margin =
        toNum(report.margin_extremes.highest.calculated_profit_margin);
    }
    if (report.margin_extremes.lowest) {
      report.margin_extremes.lowest.calculated_profit_margin =
        toNum(report.margin_extremes.lowest.calculated_profit_margin);
    }
  }

  // 5. Ensure recommendations is array
  if (!Array.isArray(report.recommendations)) {
    report.recommendations = [];
  }

  return { json: report };
}

// Map over all incoming items
return items.map(item => normalizeAI(item));

