/**
 * n8n Code node to parse and normalize AI Agent JSON response.
 * Expects raw output in item.json.output.
 */
function normalizeAI(item) {
  let raw = item.json.output;
  if (typeof raw !== 'string') {
    throw new Error('Expected output string in item.json.output');
  }

  // 1. Clean up markdown wrappers and code fences
  raw = raw
    .trim()
    .replace(/^```json\s*/, '')
    .replace(/```$/, '')
    .trim();

  // 2. Parse JSON with safe fallback
  let report;
  try {
    report = JSON.parse(raw);
  } catch (e) {
    throw new Error('Invalid JSON from AI: ' + e.message);
  }

  // 3. Helper to convert numeric fields
  const toNumber = val => {
    const num = Number(val);
    return isNaN(num) ? null : num;
  };

  // 4. Normalize daily_totals
  if (report.daily_totals) {
    report.daily_totals.total_quantity_wasted = toNumber(report.daily_totals.total_quantity_wasted);
    report.daily_totals.total_waste_cost = toNumber(report.daily_totals.total_waste_cost);
    report.daily_totals.average_cost_per_unit_wasted = toNumber(report.daily_totals.average_cost_per_unit_wasted);
  }

  // 5. Normalize by_category and by_waste_type
  ['by_category', 'by_waste_type'].forEach(section => {
    if (report[section] && typeof report[section] === 'object') {
      Object.entries(report[section]).forEach(([key, val]) => {
        val.total_cost = toNumber(val.total_cost);
        val.total_quantity = toNumber(val.total_quantity);
      });
    }
  });

  // 6. Normalize top_waste_items arrays
  if (report.top_waste_items) {
    if (Array.isArray(report.top_waste_items.by_cost)) {
      report.top_waste_items.by_cost = report.top_waste_items.by_cost.map(i => ({
        item_name: i['Item Name'],
        total_waste_cost: toNumber(i['Total Waste Cost']),
      }));
    }
    if (Array.isArray(report.top_waste_items.by_quantity)) {
      report.top_waste_items.by_quantity = report.top_waste_items.by_quantity.map(i => ({
        item_name: i['Item Name'],
        quantity_wasted: toNumber(i['Quantity Wasted']),
        unit: i['Unit'] || null,
      }));
    }
  }

  // 7. Normalize reason_summary counts
  if (report.reason_summary && typeof report.reason_summary === 'object') {
    Object.entries(report.reason_summary).forEach(([reason, count]) => {
      report.reason_summary[reason] = toNumber(count);
    });
  }

  // 8. Normalize action_evaluation.alignment
  if (report.action_evaluation?.alignment && Array.isArray(report.action_evaluation.alignment)) {
    report.action_evaluation.alignment = report.action_evaluation.alignment.map(i => ({
      reason: i.reason || null,
      action: i.action || null,
      evaluation: i.evaluation || null,
    }));
  }

  // 9. Ensure recommendations is an array with normalized entries
  if (Array.isArray(report.recommendations)) {
    report.recommendations = report.recommendations.map(r => ({
      area: r.area || null,
      issue: r.issue || null,
      recommendation: r.recommendation || null,
      reference: r.reference || null,
    }));
  } else {
    report.recommendations = [];
  }

  // Return item with normalized JSON
  return { json: report };
}

// Process all items
return items.map(item => normalizeAI(item));

