/**
 * n8n Code node: normalize AI agent JSON feedback report
 * Input: item.json.output contains the raw JSON wrapped in ```json ... ```
 */
function normalizeAI(item) {
  let raw = item.json.output;
  if (typeof raw !== 'string') {
    throw new Error('Expected output string in item.json.output');
  }

  // 1. Remove ```json and trailing ``` fences
  raw = raw
    .trim()
    .replace(/^```json\s*/, '')
    .replace(/```$/, '')
    .trim(); // Adapted from StackOverflow advice :contentReference[oaicite:1]{index=1}

  // 2. Parse JSON
  let report;
  try {
    report = JSON.parse(raw);
  } catch (e) {
    throw new Error('Invalid JSON from AI: ' + e.message);
  }

  // 3. Utility to coerce numeric values
  const toNum = v => {
    const n = Number(v);
    return isNaN(n) ? null : n;
  };

  // Normalize summary_ratings
  if (report.summary_ratings) {
    ['average_overall_rating', 'average_food_quality', 'average_service_rating', 'average_ambiance_rating']
      .forEach(key => { report.summary_ratings[key] = toNum(report.summary_ratings[key]); });
  }

  // Normalize feedback_counts
  if (report.feedback_counts) {
    ['total', 'positive', 'neutral', 'complaint']
      .forEach(key => { report.feedback_counts[key] = toNum(report.feedback_counts[key]); });
  }

  // Normalize dish_insights array
  if (Array.isArray(report.dish_insights)) {
    report.dish_insights = report.dish_insights.map(d => {
      ['average_overall_rating','average_food_quality','average_service_rating','average_ambiance_rating']
        .forEach(k => { d[k] = toNum(d[k]); });
      d.record_count = toNum(d.record_count);
      return d;
    });
  }

  // Normalize common_themes array
  if (Array.isArray(report.common_themes)) {
    report.common_themes = report.common_themes.map(t => {
      t.mentions = toNum(t.mentions);
      return t;
    });
  }

  // Normalize recommendations array
  if (Array.isArray(report.recommendations)) {
    report.recommendations = report.recommendations.map(r => ({
      area: r.area,
      priority: r.priority,
      action: r.action,
      dish_specific: r.dish_specific,
    }));
  }

  return { json: report };
}

// Process all items
return items.map(item => normalizeAI(item));

// Fetch all incoming items
const items = $input.all();

