/**
 * n8n Function/Code node: Normalize AI email output
 * Input: item.json.output contains the raw email text (possibly with fences)
 */
function normalizeEmail(item) {
  let text = item.json.output;
  if (typeof text !== 'string') {
    throw new Error('Expected raw email text in item.json.output');
  }

  // 1. Clean common wrappers & prefixes
  text = text
    .replace(/^```(?:json|text)?\n?/, '')
    .replace(/\n?```$/, '')
    .replace(/^(Here is the output:|Output:)\s*/, '');

  // 2. Trim whitespace
  text = text.trim();

  return { json: { email_body: text } };
}

// Map over all items
return items.map(item => normalizeEmail(item));
