=Analyze the user's message and any provided context to determine their primary intent. Detect the intent accurately.

User Message: "{{ $json.messageText ?? $json.transcription_text }}"

Based on the message, choose one of the following intents and return ONLY the corresponding JSON object. Do not add any extra text or markdown formatting.

- If the user is asking to generate an image, return: 
  {"intent": "generate_image", "prompt": "A detailed description of the image to generate based on the user's message"}

- If the user is explicitly asking to remember a piece of information, return: 
  {"intent": "remember", "info": "The specific fact or piece of information to remember"}

- If the user wants to set a reminder or schedule something, return: 
  {"intent": "reminder", "message": "The full reminder message"}

- For any other general conversation, chatting, or questions, return: 
  {"intent": "chat"}

Reminder-related keywords may include: remind, reminder, remember to, schedule, alert, notify, appointment, meeting, call, task, todo, deadline, due date.

Return JSON only.

parameters.jsCode:
try {
  const text = items[0].json.text || items[0].json.output;
  const clean = text.replace(/```json\n?/g, '').replace(/```/g, '').trim();
  const parsed = JSON.parse(clean);
  return [{ json: parsed }];
} catch (e) {
  // If parsing fails, default to a 'chat' intent to ensure the bot can always respond.
  return [{ json: { intent: 'chat' } }];
}

const item = items[0].json;

if (item && item.intent) {
  const intentString = item.intent.trim();
  let intentCode;

  switch (intentString) {
    case 'generate_image':
      intentCode = 1;
      break;
    case 'remember':
      intentCode = 2;
      break;
    case 'chat':
      intentCode = 3;
      break;
    case 'reminder':
      intentCode = 4;
      break;
    default:
      intentCode = 0;
  }

  return [{ json: { intentCode } }];
} else {
  return [{ json: { intentCode: -1, error: "Intent not found in input." } }];
}

// Extract recent memory
const userData = $('Extract & Validate Data1').first().json;
const memoryItems = $input.all().map(item => item.json);

let memoryContext = '';
let conversationHistory = [];

if (memoryItems && memoryItems.length > 0) {
  const validSummaries = memoryItems.filter(item => item.summary && item.summary.trim() !== '');

  if (validSummaries.length > 0) {
    memoryContext = "\n--- PREVIOUS CONTEXT ---\n";
    validSummaries.slice(0, 5).forEach((item, index) => {
      memoryContext += `${index + 1}. ${item.summary.trim()}\n`;
    });
    memoryContext += "--- END CONTEXT ---\n";
  } else {
    // fallback to using raw user messages
    memoryContext = "\n--- PREVIOUS CONTEXT (No summary available) ---\n";
    memoryItems.slice(0, 5).forEach((item, index) => {
      memoryContext += `${index + 1}. User: ${item.user_message || '...'} | Assistant: ${item.assistant_response || '...'}\n`;
    });
    memoryContext += "--- END CONTEXT ---\n";
  }

  // Save turns for future reference
  conversationHistory = memoryItems.slice(0, 3).map(item => ({
    user: item.user_message || '',
    assistant: item.assistant_response || '',
    timestamp: item.timestamp
  })).filter(conv => conv.user && conv.assistant);
}

return [{
  json: {
    ...userData,
    memoryContext,
    conversationHistory,
    hasMemory: memoryItems.length > 0,
    memoryCount: memoryItems.length
  }
}];

// Extract recent memory
const userData = $('Fetch User Memory3').first().json;
const memoryItems = $input.all().map(item => item.json);

// Initialize memory context and conversation history
let memoryContext = '';
let conversationHistory = [];

// Ensure memoryItems has data
if (memoryItems && memoryItems.length > 0) {
  // Filter out invalid summaries
  const validSummaries = memoryItems.filter(item => item.summary && item.summary.trim() !== '');

  if (validSummaries.length > 0) {
    memoryContext = "\n--- PREVIOUS CONTEXT ---\n";
    validSummaries.slice(0, 5).forEach((item, index) => {
      memoryContext += `${index + 1}. ${item.summary.trim()}\n`;
    });
    memoryContext += "--- END CONTEXT ---\n";
  } else {
    // Fallback to raw user messages if no summaries are available
    memoryContext = "\n--- PREVIOUS CONTEXT (No summary available) ---\n";
    memoryItems.slice(0, 5).forEach((item, index) => {
      memoryContext += `${index + 1}. User: ${item.user_message || '...'} | Assistant: ${item.assistant_response || '...'}\n`;
    });
    memoryContext += "--- END CONTEXT ---\n";
  }

  // Collect recent conversation history (user-message and assistant-response)
  conversationHistory = memoryItems.slice(0, 3).map(item => ({
    user: item.user_message || '',
    assistant: item.assistant_response || '',
    timestamp: item.timestamp
  })).filter(conv => conv.user && conv.assistant);
}

// Process the transcription results (Format Results)
const transcriptionText = $('Format Results1').first().json.transcription_text || '';

// Return the result with the updated memory context and transcription text
return [{
  json: {
    ...userData,
    memoryContext,
    conversationHistory,
    transcriptionText,
    hasMemory: memoryItems.length > 0,
    memoryCount: memoryItems.length
  }
}];

const output = [];

for (const item of items) {
  try {
    // Remove ```json\n and ``` wrappers
    const clean = item.json.text.replace(/```json\n?/, '').replace(/```/, '').trim();
    const parsed = JSON.parse(clean);
    output.push({ json: parsed });
  } catch (e) {
    output.push({ json: { error: 'Invalid JSON', original: item.json.text } });
  }
}

return output;

// Get original text
const originalText = $('Chat Agent2').first().json.output;

// Only escape characters that absolutely need escaping in Telegram
const sanitizedText = originalText
  .replace(/&/g, '&')
  .replace(/</g, '<')
  .replace(/>/g, '>');

return {
  json: {
    sanitizedText: sanitizedText,
    parse_mode: 'HTML'  // Using HTML mode instead of Markdown
  }
};

let input = $input.first().json.output || '';

console.log('Original input:', input);

// Remove ALL quotes (both single and double)
let cleanedInput = input.replace(/['"]+/g, '');

// Clean whitespace (replace multiple spaces with single space)
cleanedInput = cleanedInput.replace(/\s+/g, ' ').trim();

console.log('Final cleaned input:', cleanedInput);

// Return cleaned prompt
return { cleanedPrompt: cleanedInput };

const score = $json.documentSentiment?.score ?? 0;
let mood = 'neutral';

if (score > 0.25) mood = 'happy';
else if (score < -0.25) mood = 'sad';

return [{
  json: {
    mood,
    sentiment_score: score,
    text: $('Telegram Trigger1').first().json.message.text || ''
  }
}];

const message = items[0].json.message;

if (!message?.text || message.text.trim() === '') {
  return [];
}

const userData = {
  userId: message.from?.id || 'unknown',
  chatId: message.chat?.id || 'unknown',
  messageText: message.text.trim(),
  username: message.from?.username || message.from?.first_name || 'user',
  firstName: message.from?.first_name || '',
  messageId: message.message_id || 0,
  timestamp: new Date().toISOString(),
  chatType: message.chat?.type || 'private'
};

if (userData.messageText.length > 4000) {
  userData.messageText = userData.messageText.substring(0, 4000) + '...';
  userData.truncated = true;
}

return [{ json: userData }];

// Input 1: { intentCode }
const input1 = $input.all()[0]?.json ?? {};

// Input 2: { mood, sentiment_score, text }
const input2 = $input.all()[1]?.json ?? {};

// Input 3: { gfMode, chat_id }
const input3 = $input.all()[2]?.json ?? {};

// Use proper text source (input2) and normalize
const text = (input2.text || "").toLowerCase();

const memoryContext = $("Build Context2").first().json.memoryContext ?? '';

// Merge all results
const merged = {
  text,
  intentCode: input1.intentCode ?? null,
  mood: input2.mood ?? "neutral",
  gfMode: Boolean(input3.gfMode),
  mergedmemoryContext: memoryContext
};

return [
  {
    json: merged
  }
];

