const memoryItems = $input.all().map(item => item.json);
let memoryContext = '';

if (memoryItems.length > 0) {
  memoryContext = '\n--- MEMORY_AUTO CONTEXT ---\n';
  memoryItems.forEach((item, i) => {
    memoryContext += `${i + 1}. ${item.summary || item.user_message || '...'}\n`;
  });
  memoryContext += '--- END MEMORY_AUTO ---\n';
}

return [{ json: { memory_auto_context: memoryContext } }];

