function formatDate(date) {
  return date.toISOString().split('T')[0];
}

const today = new Date();

// Prior week (14 to 8 days ago)
const priorStart = new Date(today);
priorStart.setDate(today.getDate() - 14);

const priorEnd = new Date(today);
priorEnd.setDate(today.getDate() - 8);

// Last week (7 to 1 days ago)
const lastStart = new Date(today);
lastStart.setDate(today.getDate() - 7);

const lastEnd = new Date(today);
lastEnd.setDate(today.getDate() - 1);

return [
  {
    json: {
      label: "priorWeek",
      startDate: formatDate(priorStart),
      endDate: formatDate(priorEnd)
    }
  },
  {
    json: {
      label: "lastWeek",
      startDate: formatDate(lastStart),
      endDate: formatDate(lastEnd)
    }
  }
];

// Split all items by week
const prior = [];
const last = [];

for (const item of items) {
  if (item.json.week === "priorWeek") {
    prior.push(item.json);
  } else if (item.json.week === "lastWeek") {
    last.push(item.json);
  }
}

// Index prior week rows by page+query
const priorMap = new Map();
for (const row of prior) {
  const key = `${row.page}|||${row.query}`;
  priorMap.set(key, row);
}

// Match last week rows and compute deltas + formatted % changes
const results = [];

for (const row of last) {
  const key = `${row.page}|||${row.query}`;
  const previous = priorMap.get(key);
  if (!previous) continue;

  const clicksDelta = row.clicks - previous.clicks;
  const ctrDeltaRaw = row.ctr - previous.ctr;
  const impressionsDelta = row.impressions - previous.impressions;
  const positionDelta = row.position - previous.position;

  const percentChangeClicks = previous.clicks !== 0 ? `${((clicksDelta / previous.clicks) * 100).toFixed(1)}%` : null;
  const percentChangeCTR = previous.ctr !== 0 ? `${((ctrDeltaRaw / previous.ctr) * 100).toFixed(1)}%` : null;
  const percentChangeImpressions = previous.impressions !== 0 ? `${((impressionsDelta / previous.impressions) * 100).toFixed(1)}%` : null;
  const percentChangePosition = previous.position !== 0 ? `${((positionDelta / previous.position) * 100).toFixed(1)}%` : null;

  const ctrLastWeek = `${(row.ctr * 100).toFixed(1)}%`;
  const ctrPriorWeek = `${(previous.ctr * 100).toFixed(1)}%`;
  const deltaCTR = `${(ctrDeltaRaw * 100).toFixed(1)}%`;

  results.push({
    json: {
      page: row.page,
      query: row.query,

      deltaClicks: clicksDelta,
      percentChangeClicks,
      clicksLastWeek: row.clicks,
      clicksPriorWeek: previous.clicks,

      deltaCTR,
      percentChangeCTR,
      ctrLastWeek,
      ctrPriorWeek,

      deltaImpressions: impressionsDelta,
      percentChangeImpressions,
      impressionsLastWeek: row.impressions,
      impressionsPriorWeek: previous.impressions,

      deltaPosition: positionDelta,
      percentChangePosition,
      positionLastWeek: row.position,
      positionPriorWeek: previous.position
    }
  });
}

return results;

