options.customCss:
:root {
--font-family: 'Roboto', sans-serif;
  --font-weight-normal: 400;
  --font-weight-bold: 700;
  --font-size-body: 13px;
  --font-size-label: 15px;
  --font-size-test-notice: 12px;
  --font-size-input: 15px;
  --font-size-header: 20px;
  --font-size-paragraph: 16px;
  --font-size-link: 14px;
  --font-size-error: 12px;
  --font-size-html-h1: 30px;
  --font-size-html-h2: 24px;
  --font-size-html-h3: 18px;
  --font-size-html-h4: 16px;
  --font-size-html-h5: 14px;
  --font-size-html-h6: 12px;
  --font-size-subheader: 16px;
  --color-background: #f5f7fa;
  --color-test-notice-text: #e6a23d;
  --color-test-notice-bg: #fefaf6;
  --color-test-notice-border: #f6dcb7;
  --color-card-bg: linear-gradient(145deg, #ffffff, #f8f9fa);
  --color-card-border: #ced4da;
  --color-card-shadow: rgba(0, 0, 0, 0.1);
  --color-link: #007bff;
  --color-header: #343a40;
  --color-label: #495057;
  --color-input-border: #ced4da;
  --color-input-text: #495057;
  --color-focus-border: #007bff;
  --color-submit-btn-bg: #007bff;
  --color-submit-btn-text: #ffffff;
  --color-error: #dc3545;
  --color-required: #ff6d5a;
  --color-clear-button-bg: #6c757d;
  --color-html-text: #343a40;
  --color-html-link: #007bff;
  --color-header-subtext: #6c757d;
  --border-radius-card: 12px;
  --border-radius-input: 8px;
  --border-radius-clear-btn: 50%;
  --card-border-radius: 12px;
  --padding-container-top: 40px;
  --padding-card: 32px;
  --padding-test-notice-vertical: 12px;
  --padding-test-notice-horizontal: 24px;
  --margin-bottom-card: 20px;
  --padding-form-input: 14px;
  --card-padding: 32px;
  --card-margin-bottom: 20px;
  --container-width: 500px;
  --submit-btn-height: 50px;
  --checkbox-size: 20px;
  --box-shadow-card: 0px 6px 20px 0px var(--color-card-shadow);
  --opacity-placeholder: 0.6;
  --spacing-question-gap: 320px; /* Added for question spacing */
}

/* Add company logo to form header */
.form-header {
	position: relative;
	text-align: center;
	padding-top: 80px; /* Space for logo */
	margin-bottom: 20px;
}

.form-header::before {
	content: '';
	display: block;
	width: 200px; /* Adjust based on logo size */
	height: 60px; /* Adjust based on logo size */
	background-image: url('https://d1rdz15x9x7c4f.cloudfront.net/assets/payload-images/oc-blue-logo.svg');
	background-size: contain;
	background-repeat: no-repeat;
	background-position: center;
	position: absolute;
	top: 0;
	left: 50%;
	transform: translateX(-50%);
}

/* Ensure form title and description don't overlap */
.form-header h1, .form-header p {
	position: relative;
	z-index: 1;
}

/* Hide the 'Form automated with' footer */
.form-footer, [class*="form-footer"], [class*="powered-by"], [class*="branding"] {
	display: none !important;
}

/* Ensure form inputs are modern and clean */
input, select, textarea {
	border: 1px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	padding: var(--padding-form-input);
	font-size: var(--font-size-input);
	font-family: var(--font-family);
	color: var(--color-input-text);
}

input:focus, select:focus, textarea:focus {
	border-color: var(--color-focus-border);
	box-shadow: 0 0 5px rgba(0, 123, 255, 0.3);
	outline: none;
}

/* Style submit button */
button[type="submit"] {
	background-color: var(--color-submit-btn-bg);
	color: var(--color-submit-btn-text);
	border-radius: var(--border-radius-input);
	height: var(--submit-btn-height);
	font-weight: var(--font-weight-bold);
	font-size: var(--font-size-input);
	transition: background-color 0.3s ease;
}

button[type="submit"]:hover {
	background-color: #0056b3;
}
