// Intelligent candidate scoring algorithm
const candidate = $json.choices[0].message.content;
const requirements = $node['Job Requirements'].json;
const candidateData = JSON.parse(candidate);

let totalScore = 0;

// Skills matching (40% weight)
const requiredSkills = requirements.requiredSkills.split(',');
const candidateSkills = candidateData.skills || [];
const skillsMatch = requiredSkills.filter(skill => 
  candidateSkills.some(cSkill => cSkill.toLowerCase().includes(skill.toLowerCase()))
);
const skillsScore = (skillsMatch.length / requiredSkills.length) * 40;
totalScore += skillsScore;

// Experience scoring (30% weight)
const experienceYears = candidateData.experience_years || 0;
let experienceScore = 0;
if (experienceYears >= requirements.minExperience) {
  experienceScore = 30;
} else if (experienceYears >= requirements.minExperience * 0.8) {
  experienceScore = 25;
} else if (experienceYears >= requirements.minExperience * 0.6) {
  experienceScore = 20;
} else {
  experienceScore = 10;
}
totalScore += experienceScore;

// Education scoring (20% weight)
const education = candidateData.education || '';
let educationScore = 0;
if (education.toLowerCase().includes('master') || education.toLowerCase().includes('phd')) {
  educationScore = 20;
} else if (education.toLowerCase().includes('bachelor') || education.toLowerCase().includes('degree')) {
  educationScore = 15;
} else {
  educationScore = 10;
}
totalScore += educationScore;

// Role relevance (10% weight)
const previousRoles = candidateData.previous_roles || [];
const roleRelevance = previousRoles.some(role => 
  role.toLowerCase().includes('engineer') || role.toLowerCase().includes('developer')
) ? 10 : 5;
totalScore += roleRelevance;

// Determine recommendation
let recommendation = 'reject';
let priority = 'low';

if (totalScore >= 85) {
  recommendation = 'hire';
  priority = 'high';
} else if (totalScore >= requirements.passingScore) {
  recommendation = 'interview';
  priority = 'medium';
} else if (totalScore >= 60) {
  recommendation = 'phone_screen';
  priority = 'low';
}

return {
  candidateData,
  scoring: {
    skillsScore,
    experienceScore,
    educationScore,
    roleRelevance,
    totalScore: Math.round(totalScore),
    recommendation,
    priority
  },
  jobTitle: requirements.jobTitle,
  evaluatedAt: new Date().toISOString()
};
