// Advanced influencer scoring and qualification algorithm
const campaignSettings = $node['Campaign Settings'].json;
const allInfluencers = [];

// Process Instagram influencers
const instagramData = $node['Search Instagram Influencers'].json;
if (instagramData.data) {
  instagramData.data.forEach(influencer => {
    allInfluencers.push({
      platform: 'instagram',
      username: influencer.username,
      follower_count: influencer.follower_count,
      engagement_rate: influencer.engagement_rate,
      profile_url: `https://instagram.com/${influencer.username}`,
      bio: influencer.bio,
      raw_data: influencer
    });
  });
}

// Process TikTok influencers
const tiktokData = $node['Search TikTok Influencers'].json;
if (tiktokData.data) {
  tiktokData.data.forEach(influencer => {
    allInfluencers.push({
      platform: 'tiktok',
      username: influencer.username,
      follower_count: influencer.follower_count,
      engagement_rate: influencer.engagement_rate,
      profile_url: `https://tiktok.com/@${influencer.username}`,
      bio: influencer.bio,
      raw_data: influencer
    });
  });
}

// Process YouTube influencers
const youtubeData = $node['Search YouTube Influencers'].json;
if (youtubeData.items) {
  youtubeData.items.forEach(channel => {
    allInfluencers.push({
      platform: 'youtube',
      username: channel.snippet.channelTitle,
      follower_count: channel.statistics?.subscriberCount || 0,
      engagement_rate: (channel.statistics?.viewCount / channel.statistics?.subscriberCount) * 100,
      profile_url: `https://youtube.com/channel/${channel.id}`,
      bio: channel.snippet.description,
      raw_data: channel
    });
  });
}

// Filter and score influencers
const qualifiedInfluencers = allInfluencers
  .filter(influencer => {
    return influencer.follower_count >= campaignSettings.minFollowers &&
           influencer.follower_count <= campaignSettings.maxFollowers &&
           influencer.engagement_rate >= campaignSettings.minEngagementRate;
  })
  .map(influencer => {
    let score = 0;

    // Follower count scoring (30% weight)
    const followerScore = Math.min((influencer.follower_count / campaignSettings.maxFollowers) * 30, 30);
    score += followerScore;

    // Engagement rate scoring (40% weight)
    const engagementScore = Math.min((influencer.engagement_rate / 10) * 40, 40);
    score += engagementScore;

    // Platform preference scoring (20% weight)
    const platformScores = { instagram: 20, tiktok: 15, youtube: 18 };
    score += platformScores[influencer.platform] || 10;

    // Bio relevance scoring (10% weight)
    const targetKeywords = campaignSettings.targetAudience.toLowerCase().split(' ');
    const bioRelevance = targetKeywords.filter(keyword => 
      influencer.bio?.toLowerCase().includes(keyword)
    ).length;
    score += Math.min(bioRelevance * 3, 10);

    // Calculate estimated cost per post
    let costPerPost = 0;
    if (influencer.platform === 'instagram') {
      costPerPost = (influencer.follower_count / 1000) * 10; // $10 per 1k followers
    } else if (influencer.platform === 'tiktok') {
      costPerPost = (influencer.follower_count / 1000) * 8; // $8 per 1k followers
    } else if (influencer.platform === 'youtube') {
      costPerPost = (influencer.follower_count / 1000) * 25; // $25 per 1k subscribers
    }

    return {
      ...influencer,
      score: Math.round(score),
      estimated_cost: Math.round(costPerPost),
      cost_per_engagement: Math.round(costPerPost / (influencer.follower_count * influencer.engagement_rate / 100)),
      campaign_id: `${campaignSettings.campaignName}_${Date.now()}`,
      qualified_at: new Date().toISOString()
    };
  })
  .sort((a, b) => b.score - a.score)
  .slice(0, 20); // Top 20 influencers

return qualifiedInfluencers;

// Generate personalized outreach email content
const influencer = $json;
const campaignSettings = $node['Campaign Settings'].json;

// Create personalized subject line
const subjectLines = [
  `Partnership Opportunity with ${campaignSettings.campaignName}`,
  `Collaboration Proposal - ${influencer.platform.toUpperCase()} Creator`,
  `Exciting Brand Partnership for ${influencer.username}`,
  `${campaignSettings.campaignName} x ${influencer.username} - Let's Create Magic!`
];

const randomSubject = subjectLines[Math.floor(Math.random() * subjectLines.length)];

// Determine deliverables based on platform
let deliverables = [];
if (influencer.platform === 'instagram') {
  deliverables = ['1 Feed Post', '3 Stories', '1 Reel'];
} else if (influencer.platform === 'tiktok') {
  deliverables = ['1 Video Post', '2 Story Updates'];
} else if (influencer.platform === 'youtube') {
  deliverables = ['1 Sponsored Video', '1 Community Post'];
}

// Calculate campaign timeline
const startDate = new Date();
const endDate = new Date(startDate.getTime() + 14 * 24 * 60 * 60 * 1000); // 2 weeks

return {
  ...influencer,
  outreach_subject: randomSubject,
  deliverables: deliverables,
  campaign_start: startDate.toISOString(),
  campaign_end: endDate.toISOString(),
  proposal_sent_at: new Date().toISOString()
};

// Find or create influencer email
const influencer = $json;
let email = null;

// Try to extract email from bio
const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/;
const bioMatch = influencer.bio?.match(emailRegex);

if (bioMatch) {
  email = bioMatch[0];
} else {
  // Generate likely email patterns
  const username = influencer.username.toLowerCase().replace(/[^a-z0-9]/g, '');
  const possibleEmails = [
    `${username}@gmail.com`,
    `${username}@outlook.com`,
    `${username}@yahoo.com`,
    `contact@${username}.com`,
    `hello@${username}.com`,
    `${username}@${influencer.platform}.com`
  ];

  // Use the first pattern as fallback
  email = possibleEmails[0];
}

return {
  ...influencer,
  email: email,
  email_verified: bioMatch ? true : false
};
