// Advanced compliance risk analysis
const complianceSettings = $node['Compliance Settings'].json;
const jurisdictions = complianceSettings.jurisdictions.split(',');

// Collect all regulatory updates
const regulatoryUpdates = [];

// Process EU regulations
const euData = $node['Monitor EU Regulations'].json;
if (euData && euData.results) {
  euData.results.forEach(reg => {
    regulatoryUpdates.push({
      jurisdiction: 'EU',
      title: reg.title,
      url: reg.url,
      date: reg.date,
      category: 'legislation',
      impact_areas: extractImpactAreas(reg.title + ' ' + reg.abstract)
    });
  });
}

// Process US Federal Register
const usData = $node['Monitor US Federal Register'].json;
if (usData && usData.results) {
  usData.results.forEach(reg => {
    regulatoryUpdates.push({
      jurisdiction: 'US',
      title: reg.title,
      url: reg.html_url,
      date: reg.publication_date,
      category: 'federal_register',
      impact_areas: extractImpactAreas(reg.title + ' ' + reg.abstract)
    });
  });
}

// Process UK legislation
const ukData = $node['Monitor UK Legislation'].json;
if (ukData && ukData.results) {
  ukData.results.forEach(reg => {
    regulatoryUpdates.push({
      jurisdiction: 'UK',
      title: reg.title,
      url: reg.uri,
      date: reg.date,
      category: 'legislation',
      impact_areas: extractImpactAreas(reg.title)
    });
  });
}

// Get active contracts
const contracts = $node['Get Active Contracts'].json;

// Analyze compliance risks
const complianceAnalysis = [];

regulatoryUpdates.forEach(update => {
  // Find potentially affected contracts
  const affectedContracts = contracts.filter(contract => {
    const contractJurisdiction = contract.jurisdiction;
    const contractType = contract.contract_type;
    const contractClauses = contract.key_clauses || '';
    const complianceTags = contract.compliance_tags || '';

    // Check jurisdiction match
    if (contractJurisdiction !== update.jurisdiction) return false;

    // Check impact area relevance
    const isRelevant = update.impact_areas.some(area => 
      contractClauses.toLowerCase().includes(area) || 
      complianceTags.toLowerCase().includes(area)
    );

    return isRelevant;
  });

  if (affectedContracts.length > 0) {
    // Calculate risk score
    let riskScore = 0;

    // Base risk from number of affected contracts
    riskScore += Math.min(affectedContracts.length * 10, 40);

    // Risk from impact areas
    const highRiskAreas = ['data protection', 'financial services', 'healthcare', 'employment'];
    const impactRisk = update.impact_areas.filter(area => 
      highRiskAreas.some(hrArea => area.includes(hrArea))
    ).length * 15;
    riskScore += Math.min(impactRisk, 30);

    // Recency risk
    const daysSinceUpdate = (new Date() - new Date(update.date)) / (1000 * 60 * 60 * 24);
    if (daysSinceUpdate <= 7) riskScore += 20;
    else if (daysSinceUpdate <= 30) riskScore += 10;

    // Contract expiry urgency
    const nearExpiryContracts = affectedContracts.filter(contract => {
      const daysToExpiry = (new Date(contract.expiry_date) - new Date()) / (1000 * 60 * 60 * 24);
      return daysToExpiry <= 90;
    });
    riskScore += nearExpiryContracts.length * 5;

    // Determine risk level
    let riskLevel = 'low';
    let riskColor = '游릭';
    let recommendedAction = 'monitor';

    if (riskScore >= complianceSettings.criticalRiskThreshold) {
      riskLevel = 'critical';
      riskColor = '游댮';
      recommendedAction = 'emergency_review';
    } else if (riskScore >= complianceSettings.highRiskThreshold) {
      riskLevel = 'high';
      riskColor = '游';
      recommendedAction = 'immediate_review';
    } else if (riskScore >= complianceSettings.mediumRiskThreshold) {
      riskLevel = 'medium';
      riskColor = '游리';
      recommendedAction = 'scheduled_review';
    }

    complianceAnalysis.push({
      regulatory_update: update,
      affected_contracts: affectedContracts,
      risk_score: Math.round(riskScore),
      risk_level: riskLevel,
      risk_color: riskColor,
      recommended_action: recommendedAction,
      impact_assessment: {
        contract_count: affectedContracts.length,
        jurisdictions_affected: [update.jurisdiction],
        high_priority_contracts: nearExpiryContracts.length,
        estimated_review_hours: affectedContracts.length * 2
      },
      analyzed_at: new Date().toISOString()
    });
  }
});

// Helper function to extract impact areas
function extractImpactAreas(text) {
  const impactKeywords = {
    'data protection': ['gdpr', 'privacy', 'data protection', 'personal data'],
    'financial services': ['banking', 'finance', 'securities', 'investment'],
    'healthcare': ['healthcare', 'medical', 'pharmaceutical', 'hipaa'],
    'employment': ['employment', 'labor', 'workplace', 'discrimination'],
    'environmental': ['environmental', 'sustainability', 'climate', 'carbon'],
    'cybersecurity': ['cyber', 'security', 'breach', 'information security']
  };

  const areas = [];
  const lowerText = text.toLowerCase();

  Object.entries(impactKeywords).forEach(([area, keywords]) => {
    if (keywords.some(keyword => lowerText.includes(keyword))) {
      areas.push(area);
    }
  });

  return areas;
}

return complianceAnalysis.filter(analysis => analysis.risk_score > 0);
