// This function will strip out HTML tags to leave only the plain text.
function extractCleanText(html) {
  if (!html) {
    return '';
  }
  // A regular expression to remove HTML tags and extra whitespace.
  return html.replace(/<[^>]*>/g, "").replace(/\s+/g, ' ').trim();
}

// Process each email item passed to the node.
return $input.all().map(item => {
  const payload = item.json.payload || {};
  const headers = payload.headers || [];

  // A helper function to find a specific header's value.
  const getHeader = (name) => headers.find(h => h.name.toLowerCase() === name.toLowerCase())?.value || 'N/A';

  const from = getHeader('From');
  const subject = getHeader('Subject');
  const date = getHeader('Date');

  let bodyData = '';
  // Check for multipart emails first.
  if (payload.parts) {
    // Prefer plain text over HTML for simplicity.
    const plainPart = payload.parts.find(p => p.mimeType === 'text/plain');
    const htmlPart = payload.parts.find(p => p.mimeType === 'text/html');

    // Use the data from the preferred part.
    if (plainPart && plainPart.body && plainPart.body.data) {
      bodyData = plainPart.body.data;
    } else if (htmlPart && htmlPart.body && htmlPart.body.data) {
      bodyData = htmlPart.body.data;
    }
  } else if (payload.body && payload.body.data) {
    // Fallback for single-part emails.
    bodyData = payload.body.data;
  }

  // The body content is Base64 encoded, so it needs to be decoded.
  const decodedBody = Buffer.from(bodyData, 'base64').toString('utf-8');

  // Clean the decoded body to remove any lingering HTML.
  const cleanBody = extractCleanText(decodedBody);

  return {
    json: {
      from,
      subject,
      date,
      body: cleanBody
    }
  };
});

return [
  {
    json: {
      from: "N/A",
      subject: "No Email Found",
      date: new Date().toISOString(),
      body: "No new emails were found during this summary check. There is nothing to summarize at this time."
    }
  }
];

