// Process each document item
const processInvoice = (doc) => {
  // Extract header information (excluding Document Title and Processed At)
  const headerData = {
    "Invoice Number": doc?.data?.["Basic Information"]?.["Invoice Number"]?.value || "",
    "Issue Date": doc?.data?.["Basic Information"]?.["Issue Date"]?.value || "",
    "Order ID": doc?.data?.["Basic Information"]?.["Order Id/Tracking No"]?.value || "",
    "Payment Terms": doc?.data?.["Basic Information"]?.["Terms"]?.value || "",
    "Buyer Name": doc?.data?.["Buyer Detail"]?.["Name"]?.value || "",
    "Buyer Address": doc?.data?.["Buyer Detail"]?.["Address"]?.value || "",
    "Buyer GST": doc?.data?.["Buyer Detail"]?.["GST/ VAT Number"]?.value || "",
    "Seller Name": doc?.data?.["Seller Detail"]?.["Name"]?.value || "",
    "Seller Address": doc?.data?.["Seller Detail"]?.["Address"]?.value || "",
    "Seller GST": doc?.data?.["Seller Detail"]?.["GST/ VAT Number"]?.value || "",
    "Subtotal": doc?.data?.["GST & Amount"]?.["Subtotal"]?.value || 0,
    "Tax Total": doc?.data?.["GST & Amount"]?.["Tax Total"]?.value || 0,
    "Total Due": doc?.data?.["GST & Amount"]?.["Total Due"]?.value || 0
  };

  // Process line items with all original columns
  return doc?.data?.Table?.["Line Items"]?.map(item => ({
    ...headerData,  // Include all header fields
    "Sr No.": item?.["Sr No."]?.value || "",
    "Item Code": item?.["Item Code"]?.value || "",
    "Item Desc": item?.Description?.value || "",
    "HSN/SAC Code": item?.HSN?.value || "",
    "Qty": item?.Quantity?.value || 0,
    "Unit Price (INR)": item?.["Unit Price"]?.value || 0,
    "Per": item?.["Per"]?.value || "",
    "UoM": item?.UoM?.value || "",
    "Net Amount (INR)": item?.["Subtotal Line"]?.value || 0,
    "Tax Rate": item?.["Tax Rate Line"]?.value || "",
    "TGST": item?.TGST?.value || 0,
    "CGST": item?.CGST?.value || 0,
    "SGST": item?.SGST?.value || 0,
    "TCS": item?.TCS?.value || 0,
    "Gross Amt (INR)": item?.["Gross Amount"]?.value || 0,
    "Delivery Date": item?.["Delivery Date"]?.value || ""
  })) || [];
};

// Main processing
const allResults = [];
for (const item of items) {
  try {
    const doc = Array.isArray(item.json) ? item.json[0] : item.json;
    if (doc?.data) {
      allResults.push(...processInvoice(doc));
    }
  } catch (error) {
    console.log(`Error processing item: ${error.message}`);
  }
}

// Return formatted results
return allResults.length > 0 
  ? allResults.map(result => ({ json: result }))
  : [{ json: { error: "No valid data processed", raw: items[0]?.json }}];
