  "properties": {
    "amount": {
      "type": "number",
      "description": "The expense amount in the original currency"
    },
    "category": {
      "type": "string",
      "enum": [
        "Groceries",
        "Transport", 
        "Food & Dining",
        "Shopping",
        "Personal Care",
        "Health",
        "Utilities",
        "Entertainment",
        "Education",
        "Bills",
        "Subscriptions",
        "Donations",
        "Family",
        "Other"
      ],
      "description": "The expense category from predefined list"
    },
    "description": {
      "type": "string",
      "description": "Description or note about the expense"
    },
    "date": {
      "type": "string",
      "format": "date-time",
      "description": "ISO 8601 date-time string of when the expense occurred"
    },
    "user_id": {
      "type": "string",
      "description": "Telegram user ID who made the expense"
    },
    "payment_method": {
      "type": "string",
      "enum": [
        "Cash",
        "Credit Card", 
        "Wallet"
      ],
      "description": "Method used to make the payment"
    },
    "currency": {
      "type": "string",
      "enum": ["EGP", "USD", "SAR", "AED"],
      "description": "Original currency of the transaction"
    },
    "exchange_rate": {
      "type": "number",
      "description": "Exchange rate from original currency to EGP (1.0 if already EGP)"
    },
    "amount_converted": {
      "type": "number", 
      "description": "Amount converted to EGP using the exchange rate"
    }
  },
  "required": [
    "amount",
    "category", 
    "description",
    "date",
    "user_id",
    "payment_method",
    "currency",
    "exchange_rate",
    "amount_converted"
  ],
  "additionalProperties": false
}

parameters.jsCode:
// Process AI Agent output and fix date format for Google Sheets
const aiOutput = $input.first().json.output;

// Convert ISO date to Google Sheets compatible format
function formatDateForSheets(isoString) {
  const date = new Date(isoString);
  // Format as YYYY-MM-DD HH:MM:SS (Google Sheets preferred format)
  return date.toISOString().slice(0, 19).replace('T', ' ');
}

// Process the data with proper date formatting
const processedData = {
  amount: Number(aiOutput.amount) || 0,
  category: String(aiOutput.category || 'Other'),
  description: String(aiOutput.description || ''),
  date: formatDateForSheets(aiOutput.date || new Date().toISOString()),
  user_id: String(aiOutput.user_id || ''),
  payment_method: String(aiOutput.payment_method || 'Other'),
  currency: String(aiOutput.currency || 'EGP'),
  exchange_rate: Number(aiOutput.exchange_rate) || 1,
  amount_converted: Number(aiOutput.amount_converted) || Number(aiOutput.amount) || 0
};

return [{ json: processedData }];
