// Compare current API data with database records
const apiData = $input.first().json.data;
const dbData = $input.last().json;

const changes = [];
const notifications = [];

// Create a map of current DB flights for quick lookup
const dbFlights = new Map();
dbData.forEach(flight => {
  dbFlights.set(flight.flight_number, flight);
});

// Process each flight from API
apiData.forEach(apiFlight => {
  const flightNumber = apiFlight.flight.iata;
  const dbFlight = dbFlights.get(flightNumber);

  if (dbFlight) {
    // Check for changes
    const apiDeparture = new Date(apiFlight.departure.scheduled).toISOString();
    const apiArrival = new Date(apiFlight.arrival.scheduled).toISOString();
    const dbDeparture = new Date(dbFlight.departure_time).toISOString();
    const dbArrival = new Date(dbFlight.arrival_time).toISOString();

    let hasChanges = false;
    const changeDetails = {
      flight_number: flightNumber,
      changes: [],
      severity: 'low'
    };

    // Check departure time
    if (apiDeparture !== dbDeparture) {
      hasChanges = true;
      const timeDiff = Math.abs(new Date(apiDeparture) - new Date(dbDeparture)) / (1000 * 60);
      changeDetails.changes.push({
        type: 'departure_time',
        old_value: dbDeparture,
        new_value: apiDeparture,
        time_difference_minutes: Math.round(timeDiff)
      });
      if (timeDiff > 60) changeDetails.severity = 'high';
      else if (timeDiff > 30) changeDetails.severity = 'medium';
    }

    // Check arrival time
    if (apiArrival !== dbArrival) {
      hasChanges = true;
      const timeDiff = Math.abs(new Date(apiArrival) - new Date(dbArrival)) / (1000 * 60);
      changeDetails.changes.push({
        type: 'arrival_time',
        old_value: dbArrival,
        new_value: apiArrival,
        time_difference_minutes: Math.round(timeDiff)
      });
      if (timeDiff > 60) changeDetails.severity = 'high';
      else if (timeDiff > 30) changeDetails.severity = 'medium';
    }

    // Check status
    if (apiFlight.flight_status !== dbFlight.status) {
      hasChanges = true;
      changeDetails.changes.push({
        type: 'status',
        old_value: dbFlight.status,
        new_value: apiFlight.flight_status
      });
      if (['cancelled', 'diverted'].includes(apiFlight.flight_status)) {
        changeDetails.severity = 'critical';
      }
    }

    // Check gate
    if (apiFlight.departure.gate !== dbFlight.gate) {
      hasChanges = true;
      changeDetails.changes.push({
        type: 'gate',
        old_value: dbFlight.gate,
        new_value: apiFlight.departure.gate
      });
    }

    if (hasChanges) {
      changes.push({
        ...changeDetails,
        airline: apiFlight.airline.name,
        route: `${apiFlight.departure.airport} â†’ ${apiFlight.arrival.airport}`,
        updated_at: new Date().toISOString()
      });
    }
  }
});

// Generate notifications based on severity
changes.forEach(change => {
  if (['high', 'critical'].includes(change.severity)) {
    notifications.push({
      type: 'urgent',
      flight_number: change.flight_number,
      message: `Flight ${change.flight_number} has ${change.severity} changes: ${change.changes.map(c => c.type).join(', ')}`,
      severity: change.severity,
      changes: change.changes,
      timestamp: new Date().toISOString()
    });
  }
});

return [{
  json: {
    total_changes: changes.length,
    changes: changes,
    notifications: notifications,
    processed_at: new Date().toISOString()
  }
}];

