// Process bookings for fare tracking
const bookings = $input.all()[0].json;
const processedBookings = [];

for (const booking of bookings) {
  // Format dates and create search parameters
  const departureDate = new Date(booking.departure_date).toISOString().split('T')[0];
  const searchParams = {
    booking_id: booking.booking_id,
    origin: booking.origin,
    destination: booking.destination,
    departure_date: departureDate,
    airline: booking.airline,
    flight_number: booking.flight_number,
    booking_class: booking.booking_class,
    original_fare: parseFloat(booking.original_fare),
    passenger_name: booking.passenger_name,
    email: booking.email,
    phone: booking.phone,
    confirmation_code: booking.confirmation_code,
    current_lowest_fare: parseFloat(booking.current_lowest_fare || booking.original_fare),
    booking_date: booking.booking_date,
    last_checked: new Date().toISOString()
  };

  processedBookings.push(searchParams);
}

return processedBookings.map(booking => ({ json: booking }));

// Analyze fare data and detect drops
const bookingData = $('prepare-fare-search').item.json;
const amadeusFares = $('search-current-fares').item.json.data || [];
const skyscannerFares = $('search-skyscanner-fares').item.json.Quotes || [];

const originalFare = bookingData.original_fare;
const currentLowestFare = bookingData.current_lowest_fare;
let newLowestFare = originalFare;
let fareSource = 'original';
let availableFares = [];

// Process Amadeus fares
amadeusFares.forEach(offer => {
  if (offer.price && offer.price.total) {
    const fare = parseFloat(offer.price.total);
    availableFares.push({
      source: 'amadeus',
      fare: fare,
      airline: offer.validatingAirlineCodes?.[0] || 'Unknown',
      flight_number: offer.itineraries?.[0]?.segments?.[0]?.number || 'N/A',
      booking_class: offer.travelerPricings?.[0]?.fareDetailsBySegment?.[0]?.class || 'Unknown'
    });

    if (fare < newLowestFare) {
      newLowestFare = fare;
      fareSource = 'amadeus';
    }
  }
});

// Process Skyscanner fares
skyscannerFares.forEach(quote => {
  if (quote.MinPrice) {
    const fare = parseFloat(quote.MinPrice);
    availableFares.push({
      source: 'skyscanner',
      fare: fare,
      airline: 'Various',
      flight_number: 'Multiple',
      booking_class: 'Economy'
    });

    if (fare < newLowestFare) {
      newLowestFare = fare;
      fareSource = 'skyscanner';
    }
  }
});

// Calculate savings and determine action
const originalSavings = originalFare - newLowestFare;
const currentSavings = currentLowestFare - newLowestFare;
const savingsPercentage = ((originalFare - newLowestFare) / originalFare * 100).toFixed(1);

// Determine fare trend
let trend = 'stable';
if (newLowestFare < currentLowestFare * 0.95) trend = 'dropping';
else if (newLowestFare > currentLowestFare * 1.05) trend = 'rising';

// Determine notification priority
let priority = 'low';
let actionRecommended = false;

if (originalSavings >= 100 || savingsPercentage >= 15) {
  priority = 'high';
  actionRecommended = true;
} else if (originalSavings >= 50 || savingsPercentage >= 8) {
  priority = 'medium';
  actionRecommended = true;
}

// Check airline-specific refund policies
const refundEligible = checkRefundEligibility(bookingData.airline, originalSavings, bookingData.booking_date);

function checkRefundEligibility(airline, savings, bookingDate) {
  const daysSinceBooking = Math.floor((new Date() - new Date(bookingDate)) / (1000 * 60 * 60 * 24));

  // Airline-specific policies (simplified)
  const policies = {
    'AA': { min_savings: 50, max_days: 24 }, // American Airlines
    'DL': { min_savings: 75, max_days: 24 }, // Delta
    'UA': { min_savings: 50, max_days: 24 }, // United
    'SW': { min_savings: 25, max_days: 365 }, // Southwest (more flexible)
    'JB': { min_savings: 50, max_days: 24 }, // JetBlue
  };

  const policy = policies[airline] || { min_savings: 75, max_days: 24 };

  return savings >= policy.min_savings && daysSinceBooking <= policy.max_days;
}

return [{
  json: {
    ...bookingData,
    new_lowest_fare: newLowestFare,
    fare_source: fareSource,
    original_savings: Math.round(originalSavings * 100) / 100,
    current_savings: Math.round(currentSavings * 100) / 100,
    savings_percentage: parseFloat(savingsPercentage),
    fare_trend: trend,
    priority: priority,
    action_recommended: actionRecommended,
    refund_eligible: refundEligible,
    available_fares: availableFares.sort((a, b) => a.fare - b.fare).slice(0, 5),
    check_timestamp: new Date().toISOString(),
    days_until_departure: Math.ceil((new Date(bookingData.departure_date) - new Date()) / (1000 * 60 * 60 * 24))
  }
}];

