// Process flight data and check for fare changes
const currentFlights = items[0].json.data || [];
const processedFlights = [];

for (const flight of currentFlights) {
  if (flight.flight_status === 'scheduled' && flight.departure) {
    // Extract fare information (you may need to adapt based on API response)
    const flightInfo = {
      flight_number: flight.flight.iata,
      airline: flight.airline.name,
      departure: flight.departure.airport,
      arrival: flight.arrival.airport,
      departure_time: flight.departure.scheduled,
      arrival_time: flight.arrival.scheduled,
      // Mock fare data - replace with actual fare from your chosen API
      current_fare: Math.floor(Math.random() * 500) + 200,
      route: `${flight.departure.iata}-${flight.arrival.iata}`,
      timestamp: new Date().toISOString()
    };

    processedFlights.push(flightInfo);
  }
}

return processedFlights.map(flight => ({ json: flight }));

// Format alert message
