// Get current flight data from previous node (Process Flight Data)
const currentFlightsItems = $('Process Flight Data').all();

// Get stored fare data from Google Sheets node
const sheetsData = $('Previous flight data').all();

console.log('Current flights items:', currentFlightsItems.length);
console.log('Sheets data items:', sheetsData.length);

// Build lookup object from Google Sheets data (remove duplicates)
const storedFares = {};
const uniqueSheetRows = new Map();

// Remove duplicates from sheets data first
for (const row of sheetsData) {
  const rowData = row.json;
  if (rowData.flight_number && rowData.route) {
    const routeKey = `${rowData.flight_number}_${rowData.route}`;

    // Keep only the first occurrence of each flight
    if (!uniqueSheetRows.has(routeKey)) {
      uniqueSheetRows.set(routeKey, rowData);
      storedFares[routeKey] = parseFloat(rowData.current_fare || 0);
    }
  }
}

console.log('Stored fares lookup:', Object.keys(storedFares));

const alertFlights = [];
const fareUpdates = [];

// Process each current flight item
for (const flightItem of currentFlightsItems) {
  const flightData = flightItem.json;

  if (!flightData.flight_number || !flightData.route) {
    console.log('Skipping invalid flight data:', flightData);
    continue;
  }

  const routeKey = `${flightData.flight_number}_${flightData.route}`;
  const currentFare = parseFloat(flightData.current_fare || 0);
  const previousFare = storedFares[routeKey];

  console.log(`Processing ${routeKey}: Current=${currentFare}, Previous=${previousFare}`);

  if (previousFare && previousFare !== currentFare && currentFare > 0) {
    const fareChange = currentFare - previousFare;
    const percentageChange = (fareChange / previousFare) * 100;

    console.log(`${routeKey}: Change=${fareChange}, Percentage=${percentageChange.toFixed(2)}%`);

    // Alert if fare decreased by 10% or more, or increased by 15% or more
    if (percentageChange <= -10 || percentageChange >= 15) {
      alertFlights.push({
        ...flightData,
        previous_fare: previousFare,
        fare_change: fareChange,
        percentage_change: Math.round(percentageChange * 100) / 100,
        alert_type: percentageChange < 0 ? 'PRICE_DROP' : 'PRICE_INCREASE',
        alert_message: percentageChange < 0 
          ? `ðŸ”¥ PRICE DROP: ${Math.abs(percentageChange).toFixed(1)}% decrease!` 
          : `âš ï¸ PRICE INCREASE: ${percentageChange.toFixed(1)}% increase!`
      });

      console.log(`ALERT TRIGGERED for ${routeKey}: ${percentageChange < 0 ? 'DROP' : 'INCREASE'} of ${Math.abs(percentageChange).toFixed(2)}%`);
    }
  } else if (!previousFare) {
    console.log(`New flight detected: ${routeKey}`);
  } else if (currentFare <= 0) {
    console.log(`Invalid current fare for ${routeKey}: ${currentFare}`);
  }

  // Prepare fare updates for Google Sheets (to update stored fares)
  if (currentFare > 0) {
    fareUpdates.push({
      row_number: uniqueSheetRows.get(routeKey)?.row_number || null,
      flight_number: flightData.flight_number,
      airline: flightData.airline || 'Unknown',
      departure: flightData.departure || 'Unknown',
      arrival: flightData.arrival || 'Unknown',
      departure_time: flightData.departure_time || '',
      arrival_time: flightData.arrival_time || '',
      current_fare: currentFare,
      route: flightData.route,
      timestamp: flightData.timestamp || new Date().toISOString(),
      last_updated: new Date().toISOString()
    });
  }
}

console.log(`Total alerts generated: ${alertFlights.length}`);
if (alertFlights.length > 0) {
  console.log(`Alerts for flights:`, alertFlights.map(f => f.flight_number));
}

// Store fare updates in node context for the Google Sheets update node
$node["Code"].context = { fareUpdates };

// If no alerts, return empty array but still process
if (alertFlights.length === 0) {
  console.log('No fare alerts triggered');
  return [];
}

// Return alert flights for notification processing
return alertFlights.map(flight => ({ json: flight }));
