const events = $input.all();
const today = new Date();

// Get Sunday of current week (start of week)
const sunday = new Date(today);
sunday.setDate(today.getDate() - today.getDay());
sunday.setHours(0, 0, 0, 0);

// Create array of all 7 days (Sunday-Saturday)
const allDays = [];
for (let i = 0; i < 7; i++) {
  const day = new Date(sunday);
  day.setDate(sunday.getDate() + i);
  allDays.push(day);
}

const dayNames = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
console.log('Analyzing full week:', allDays.map((d, i) => `${dayNames[i]} ${d.toDateString()}`));

const allFocusSlots = [];

// Process each day of the week
for (let dayIndex = 0; dayIndex < allDays.length; dayIndex++) {
  const currentDay = allDays[dayIndex];
  const dayName = dayNames[dayIndex];

  const workStart = new Date(currentDay);
  workStart.setHours(9, 0, 0, 0);
  const workEnd = new Date(currentDay);
  workEnd.setHours(17, 0, 0, 0);

  // Filter events for this specific day
  const dayEvents = events
    .filter(event => {
      const eventStart = new Date(event.json.start.dateTime || event.json.start.date);
      return eventStart.toDateString() === currentDay.toDateString();
    })
    .sort((a, b) => {
      const aStart = new Date(a.json.start.dateTime || a.json.start.date);
      const bStart = new Date(b.json.start.dateTime || b.json.start.date);
      return aStart - bStart;
    });

  // Calculate total booked time for this day
  let totalBookedMinutes = 0;
  for (const event of dayEvents) {
    const startTime = new Date(event.json.start.dateTime || event.json.start.date);
    const endTime = new Date(event.json.end.dateTime || event.json.end.date);

    const effectiveStart = new Date(Math.max(startTime.getTime(), workStart.getTime()));
    const effectiveEnd = new Date(Math.min(endTime.getTime(), workEnd.getTime()));

    if (effectiveStart < effectiveEnd) {
      totalBookedMinutes += (effectiveEnd - effectiveStart) / (1000 * 60);
    }
  }

  const totalBookedHours = totalBookedMinutes / 60;
  console.log(`${dayName} ${currentDay.toDateString()}: ${totalBookedHours.toFixed(1)} hours booked`);

  // Only process days with 6+ hours booked
  if (totalBookedHours >= 6) {
    console.log(`Creating focus time for ${dayName} (${totalBookedHours.toFixed(1)} hours booked)`);

    const freeSlots = [];
    let currentTime = new Date(workStart);

    // Check time before first event
    if (dayEvents.length > 0) {
      const firstEventStart = new Date(dayEvents[0].json.start.dateTime || dayEvents[0].json.start.date);
      if (currentTime < firstEventStart) {
        const slotEnd = new Date(Math.min(firstEventStart.getTime(), workEnd.getTime()));
        if (currentTime < slotEnd) {
          freeSlots.push({
            start: new Date(currentTime),
            end: slotEnd
          });
        }
      }
    }

    // Check time between events
    for (let i = 0; i < dayEvents.length - 1; i++) {
      const currentEventEnd = new Date(dayEvents[i].json.end.dateTime || dayEvents[i].json.end.date);
      const nextEventStart = new Date(dayEvents[i + 1].json.start.dateTime || dayEvents[i + 1].json.start.date);

      const slotStart = new Date(Math.max(currentEventEnd.getTime(), workStart.getTime()));
      const slotEnd = new Date(Math.min(nextEventStart.getTime(), workEnd.getTime()));

      if (slotStart < slotEnd) {
        freeSlots.push({
          start: slotStart,
          end: slotEnd
        });
      }
    }

    // Check time after last event
    if (dayEvents.length > 0) {
      const lastEventEnd = new Date(dayEvents[dayEvents.length - 1].json.end.dateTime || dayEvents[dayEvents.length - 1].json.end.date);
      const slotStart = new Date(Math.max(lastEventEnd.getTime(), workStart.getTime()));

      if (slotStart < workEnd) {
        freeSlots.push({
          start: slotStart,
          end: new Date(workEnd)
        });
      }
    } else {
      // No events this day, but 6+ hours requirement met somehow (shouldn't happen, but handle it)
      console.log(`${dayName}: No events found but met 6+ hour criteria`);
    }

    // Filter slots that are at least 15 minutes long and add to all slots
    const validSlots = freeSlots.filter(slot => 
      (slot.end - slot.start) >= 15 * 60 * 1000
    );

    validSlots.forEach(slot => {
      allFocusSlots.push({
        start: slot.start.toISOString(),
        end: slot.end.toISOString(),
        duration: Math.round((slot.end - slot.start) / (1000 * 60)),
        summary: "Focus Time",
        day: currentDay.toDateString(),
        dayName: dayName,
        bookedHours: totalBookedHours.toFixed(1)
      });
    });
  }
}

console.log(`Found ${allFocusSlots.length} focus time slots across the full week`);

if (allFocusSlots.length === 0) {
  return [{
    json: {
      shouldCreateFocusTime: false,
      message: 'No days this week have 6+ hours booked. Focus time not needed.',
      weekAnalyzed: allDays.map((d, i) => `${dayNames[i]} ${d.toDateString()}`)
    }
  }];
}

return [{
  json: {
    shouldCreateFocusTime: true,
    totalSlotsFound: allFocusSlots.length,
    freeSlots: allFocusSlots,
    weekAnalyzed: allDays.map((d, i) => `${dayNames[i]} ${d.toDateString()}`)
  }
}];

const items = $input.all();
const summary = {
  message: 'Focus time blocks created successfully for the week',
  totalSlots: items.length,
  slots: items.map(item => ({
    start: item.json.start.dateTime,
    end: item.json.end.dateTime,
    summary: item.json.summary
  }))
};

console.log('Weekly Focus Time Creation Summary:', JSON.stringify(summary, null, 2));

return [{
  json: summary
}];

const data = $input.first().json;
console.log('Weekly Focus Time Analysis:', data.message);

return [{
  json: {
    message: data.message,
    weekAnalyzed: data.weekAnalyzed,
    action: 'no_focus_time_needed'
  }
}];
