// Parse AI response and prepare flight search
const aiResponse = $input.first().json.choices[0].message.content;
let bookingData;

try {
  // Try to parse as JSON first
  bookingData = JSON.parse(aiResponse);
} catch {
  // If not JSON, extract info from text
  bookingData = {
    type: 'flight',
    origin: extractValue(aiResponse, ['from', 'origin', 'departure']),
    destination: extractValue(aiResponse, ['to', 'destination', 'arrival']),
    departDate: extractValue(aiResponse, ['depart', 'departure date', 'leaving']),
    passengers: extractValue(aiResponse, ['passenger', 'traveler', 'people']) || 1
  };
}

function extractValue(text, keywords) {
  for (const keyword of keywords) {
    const regex = new RegExp(`${keyword}[:\s]+([^\n,]+)`, 'i');
    const match = text.match(regex);
    if (match) return match[1].trim();
  }
  return null;
}

// Prepare flight search parameters
const flightSearch = {
  origin: bookingData.origin || 'NYC',
  destination: bookingData.destination || 'LAX',
  departureDate: bookingData.departDate || new Date().toISOString().split('T')[0],
  passengers: parseInt(bookingData.passengers) || 1,
  class: bookingData.class || 'economy'
};

return { json: { bookingData, flightSearch, originalRequest: $input.first().json } };

// Parse AI response and prepare hotel search
const aiResponse = $input.first().json.choices[0].message.content;
let bookingData;

try {
  bookingData = JSON.parse(aiResponse);
} catch {
  bookingData = {
    type: 'hotel',
    destination: extractValue(aiResponse, ['destination', 'city', 'location']),
    checkIn: extractValue(aiResponse, ['check in', 'arrival', 'checkin']),
    checkOut: extractValue(aiResponse, ['check out', 'departure', 'checkout']),
    guests: extractValue(aiResponse, ['guest', 'people', 'traveler']) || 2,
    rooms: extractValue(aiResponse, ['room']) || 1
  };
}

function extractValue(text, keywords) {
  for (const keyword of keywords) {
    const regex = new RegExp(`${keyword}[:\s]+([^\n,]+)`, 'i');
    const match = text.match(regex);
    if (match) return match[1].trim();
  }
  return null;
}

// Prepare hotel search parameters
const hotelSearch = {
  destination: bookingData.destination || 'New York',
  checkIn: bookingData.checkIn || new Date().toISOString().split('T')[0],
  checkOut: bookingData.checkOut || new Date(Date.now() + 86400000).toISOString().split('T')[0],
  guests: parseInt(bookingData.guests) || 2,
  rooms: parseInt(bookingData.rooms) || 1
};

return { json: { bookingData, hotelSearch, originalRequest: $input.first().json } };

// Process flight search results and prepare booking
const searchResults = $input.first().json;
const bookingData = $input.first().json;

// Mock flight results if API fails
const flights = searchResults.data || [
  {
    flight_number: 'AA123',
    airline: 'American Airlines',
    departure: { airport: bookingData.flightSearch?.origin || 'NYC', time: '09:00' },
    arrival: { airport: bookingData.flightSearch?.destination || 'LAX', time: '12:00' },
    price: 299,
    duration: '5h 30m'
  },
  {
    flight_number: 'DL456',
    airline: 'Delta Airlines', 
    departure: { airport: bookingData.flightSearch?.origin || 'NYC', time: '14:00' },
    arrival: { airport: bookingData.flightSearch?.destination || 'LAX', time: '17:30' },
    price: 325,
    duration: '5h 30m'
  }
];

// Select best flight (lowest price)
const selectedFlight = flights.sort((a, b) => (a.price || 299) - (b.price || 325))[0];

// Prepare booking confirmation
const booking = {
  type: 'flight',
  status: 'confirmed',
  confirmation: `FL${Date.now()}`,
  details: selectedFlight,
  passenger: {
    name: 'John Doe', // In real workflow, get from user data
    email: 'john@example.com'
  },
  total: selectedFlight.price || 299
};

return { json: { booking, searchResults: flights } };

// Process hotel search results and prepare booking
const searchResults = $input.first().json;
const bookingData = $input.first().json;

// Mock hotel results if API fails
const hotels = searchResults.hotels || [
  {
    name: 'Grand Plaza Hotel',
    location: bookingData.hotelSearch?.destination || 'New York',
    rating: 4.5,
    price_per_night: 150,
    amenities: ['WiFi', 'Pool', 'Gym'],
    room_type: 'Deluxe Room'
  },
  {
    name: 'City Center Inn',
    location: bookingData.hotelSearch?.destination || 'New York',
    rating: 4.0,
    price_per_night: 120,
    amenities: ['WiFi', 'Breakfast'],
    room_type: 'Standard Room'
  }
];

// Select best hotel (highest rating)
const selectedHotel = hotels.sort((a, b) => (b.rating || 4) - (a.rating || 3.5))[0];

// Calculate total nights
const checkIn = new Date(bookingData.hotelSearch?.checkIn || new Date());
const checkOut = new Date(bookingData.hotelSearch?.checkOut || new Date(Date.now() + 86400000));
const nights = Math.ceil((checkOut - checkIn) / (1000 * 60 * 60 * 24)) || 1;

// Prepare booking confirmation
const booking = {
  type: 'hotel',
  status: 'confirmed',
  confirmation: `HT${Date.now()}`,
  details: selectedHotel,
  guest: {
    name: 'John Doe',
    email: 'john@example.com'
  },
  nights: nights,
  total: (selectedHotel.price_per_night || 120) * nights
};

return { json: { booking, searchResults: hotels } };
