const bookingData = $input.first();
const geoData = $node['IP Geolocation Check'].json;
const agentResponse = $node['AI Agent'].json.output;

// Parse AI agent response
let aiAnalysis;
try {
  // Extract JSON from agent response
  const jsonMatch = agentResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    aiAnalysis = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (e) {
  console.error('AI Analysis parsing error:', e);
  // Fallback analysis
  aiAnalysis = {
    risk_score: 50,
    risk_level: 'MEDIUM',
    reasons: ['AI analysis parsing failed - manual review required'],
    fraud_indicators: ['System error'],
    recommendation: 'REVIEW'
  };
}

// Calculate additional rule-based risk factors
let additionalRiskScore = 0;
let riskFactors = [];

// Amount-based risk assessment
const amount = parseFloat(bookingData.booking_amount);
if (amount > 10000) {
  additionalRiskScore += 25;
  riskFactors.push('Very high transaction amount (>$10,000)');
} else if (amount > 5000) {
  additionalRiskScore += 15;
  riskFactors.push('High transaction amount (>$5,000)');
} else if (amount < 10) {
  additionalRiskScore += 10;
  riskFactors.push('Unusually low amount - potential testing');
}

// Geographic risk assessment
if (geoData.country === 'Unknown' || geoData.status === 'fail') {
  additionalRiskScore += 20;
  riskFactors.push('IP geolocation failed or unknown');
} else if (['TOR', 'VPN', 'Proxy'].includes(geoData.proxy || '')) {
  additionalRiskScore += 15;
  riskFactors.push('VPN/Proxy detected');
}

// Time-based pattern analysis
const bookingTime = new Date(bookingData.booking_time);
const hour = bookingTime.getHours();
if (hour < 6 || hour > 23) {
  additionalRiskScore += 10;
  riskFactors.push('Unusual booking time (late night/early morning)');
}

// Payment method risk evaluation
const highRiskPayments = ['cryptocurrency', 'prepaid_card', 'gift_card', 'mobile_payment'];
if (highRiskPayments.includes(bookingData.payment_method?.toLowerCase())) {
  additionalRiskScore += 15;
  riskFactors.push(`High-risk payment method: ${bookingData.payment_method}`);
}

// Combine AI score with rule-based score
const aiRiskScore = aiAnalysis.risk_score || 0;
const combinedRiskScore = Math.min(100, Math.round((aiRiskScore + additionalRiskScore) / 2));

// Determine final risk level
let finalRiskLevel;
if (combinedRiskScore >= 80) finalRiskLevel = 'CRITICAL';
else if (combinedRiskScore >= 40) finalRiskLevel = 'HIGH';
else if (combinedRiskScore >= 40) finalRiskLevel = 'MEDIUM';
else finalRiskLevel = 'LOW';

// Combine all risk factors and indicators
const allRiskFactors = [...riskFactors, ...(aiAnalysis.reasons || [])];
const allFraudIndicators = [...(aiAnalysis.fraud_indicators || []), 'Rule-based analysis completed'];

// Determine final recommendation
let finalRecommendation;
if (finalRiskLevel === 'CRITICAL') finalRecommendation = 'BLOCK';
else if (finalRiskLevel === 'HIGH') finalRecommendation = 'REVIEW';
else finalRecommendation = 'APPROVE';

return {
  user_id: bookingData.user_id,
  booking_amount: bookingData.booking_amount,
  risk_score: combinedRiskScore,
  risk_level: finalRiskLevel,
  recommendation: aiAnalysis.recommendation || finalRecommendation,
  risk_factors: allRiskFactors,
  fraud_indicators: allFraudIndicators,
  ai_analysis: aiAnalysis.reasons || ['No AI analysis available'],
  location: `${geoData.city || 'Unknown'}, ${geoData.country || 'Unknown'}`,
  ip_address: bookingData.ip_address,
  timestamp: new Date().toISOString(),
  session_id: bookingData.session_id,
  payment_method: bookingData.payment_method,
  ai_raw_score: aiRiskScore,
  rule_based_score: additionalRiskScore
};

