parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "ad_image": {
      "type": "string",
      "description": "Description of the ad image."
    },
    "ad_text": {
      "type": "string",
      "description": "Text used in the Meta ad."
    },
    "analysis": {
      "type": "object",
      "properties": {
        "strengths": {
          "type": "array",
          "items": { "type": "string" },
          "description": "List of at least 3 strengths with explanations."
        },
        "weaknesses": {
          "type": "array",
          "items": { "type": "string" },
          "description": "List of at least 3 weaknesses with explanations."
        },
        "potential_improvements": {
          "type": "array",
          "items": { "type": "string" },
          "description": "Specific, actionable improvement suggestions."
        }
      },
      "required": ["strengths", "weaknesses", "potential_improvements"]
    },
    "scoring": {
      "type": "object",
      "properties": {
        "overall_score": {
          "type": "number",
          "minimum": 1,
          "maximum": 5,
          "description": "Overall score from 1 to 5."
        },
        "justification": {
          "type": "string",
          "description": "Reasoning behind the given score."
        }
      },
      "required": ["overall_score", "justification"]
    },
    "tldr": {
      "type": "string",
      "description": "One or two sentence summary of effectiveness and key improvements."
    }
  },
  "required": ["ad_image", "ad_text", "analysis", "scoring", "tldr"]
}
