// Filter transactions for the previous month
const rows = items.map(item => item.json);

const now = new Date();
const lastMonthStart = new Date(now.getFullYear(), now.getMonth() - 1, 1); // First day of previous month
const lastMonthEnd = new Date(now.getFullYear(), now.getMonth(), 0);   // Last day of previous month

const filtered = rows.filter(row => {
  // Ensure 'Date' column exists and is a valid date format
  if (!row.Date) return false;
  const date = new Date(row.Date); 
  return date >= lastMonthStart && date <= lastMonthEnd;
});

return filtered.map(row => ({ json: row }));
