// Get the input data
const inputData = $input.all()[0].json;
// Extract listings array from result - FIXED: use real_estate_listings
const listings = inputData.result.real_estate_listings || inputData.result.listings || inputData.result.properties || inputData.listings || [];

function formatForTelegram(listing) {
