    address,
    price,
    beds,
    baths,
    sqft,
    lot_size,
    year_built,
    year_bult, // Note: there's a typo in your data (year_bult instead of year_built)
    property_type,
    listing_url
  } = listing;

  // Handle null/undefined values
  const safeAddress = address || 'Address not available';
  const safePrice = price || 'Price not listed';
  const safeBeds = beds || 'N/A';
  const safeBaths = baths || 'N/A';
  const safeSqft = sqft || 'N/A';
  const safeLotSize = lot_size && lot_size !== '[null]' ? lot_size : null;
  // Handle both year_built and year_bult (typo in data)
  const safeYearBuilt = (year_built || year_bult) && (year_built || year_bult) !== 'N/A' ? (year_built || year_bult) : null;
  const safePropertyType = property_type || 'Property';
  const safeUrl = listing_url || '#';

  let message = `üè† **NEW ${safePropertyType.toUpperCase()} LISTING**

üìç **Address:** ${safeAddress}
üí∞ **Price:** ${safePrice}
üõèÔ∏è **Beds:** ${safeBeds} | üöø **Baths:** ${safeBaths}
üìê **Size:** ${safeSqft} sqft`;

  // Add optional fields if available
  if (safeLotSize) {
    message += `\nüå± **Lot Size:** ${safeLotSize}`;
  }

  if (safeYearBuilt) {
    message += `\nüóìÔ∏è **Year Built:** ${safeYearBuilt}`;
  }

  message += `\n\nüîó [View Full Details](${safeUrl})

‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ
üïê Listed: ${new Date().toLocaleString()}`;

  return message;
}

// Debug log
console.log(`Found ${listings.length} listings`);

// Return each listing as separate execution with formatted Telegram text
return listings.map(listing => ({
  json: {
    text: formatForTelegram(listing),
    // Keep original data for reference if needed
    address: listing.address,
    price: listing.price,
    beds: listing.beds,
    baths: listing.baths,
    sqft: listing.sqft,
    lot_size: listing.lot_size,
    year_built: listing.year_built || listing.year_bult, // Handle typo
    property_type: listing.property_type,
    listing_url: listing.listing_url
  }
}));
